import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const RollerCoasterText = () => {
  const text =
    "Recognized as a Nigerian business leader, we employed 18,000+ people and fueled industrial growth.";
  const words = text.split(" ");

  const wrapperStyle = {
    overflow: "hidden",
    whiteSpace: "nowrap",
    backgroundColor: "#f8f9fa",
  color: "rgba(218, 15, 15, 0.3)",
    position: "relative",
  };

  const trackStyle = {
    display: "inline-block",
    fontSize:"150px",
    animation: "scroll-left 10s linear infinite",
  };

  const spanStyle = (index) => ({
    display: "inline-block",
    marginRight: "1rem",
    animation: "wave 2s ease-in-out infinite",
    animationDelay: `${index * 0.2}s`,
  });

  const keyframes = `
    @keyframes scroll-left {
      0% { transform: translateX(100%); }
      100% { transform: translateX(-100%); }
    }

    @keyframes wave {
      0%, 100% { transform: translateY(0); }
      50% { transform: translateY(-20px); }
    }
  `;

  return (
    <>
      <style>{keyframes}</style>
      <Container fluid style={wrapperStyle}>
        <Row>
          <Col>
            <div style={trackStyle}>
              {words.map((word, index) => (
                <span key={index} style={spanStyle(index)}>
                  {word}
                </span>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default RollerCoasterText;
