import React, { useEffect, useState } from "react";

const API = "http://localhost:5000/germany";

const GermanyMatchCard = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch matches
  const fetchMatches = () => {
    setLoading(true);
    fetch(API)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setMatches(data);
        } else if (Array.isArray(data.germany)) {
          setMatches(data.germany);
        } else {
          setMatches([]);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching matches:", err);
        setMatches([]);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchMatches();
  }, []);

  // Handle delete
  const handleDelete = async (id) => {
    if (!window.confirm("Delete this match?")) return;
    await fetch(`${API}/${id}`, { method: "DELETE" })
      .then((res) => res.json())
      .then(() => fetchMatches())
      .catch((err) => console.error("Error deleting match:", err));
  };

  return (
    <div className="container mt-4">
      <h3 className="mb-3">ðŸ‡©ðŸ‡ª Germany Matches</h3>

      {/* Add Match Form */}

      {loading ? (
        <p>Loading...</p>
      ) : matches.length === 0 ? (
        <p>No matches found.</p>
      ) : (
        <table className="table table-striped table-bordered">
          <thead className="table-dark">
            <tr>
              <th>Time</th>
              <th>Home</th>
              <th>Away</th>
              <th>Tip</th>
              <th>O/U</th>
              <th>Odds (1 - X - 2)</th>
              <th>Double (1X - 12 - X2)</th>
            </tr>
          </thead>
          <tbody>
            {matches.map((match, idx) => (
              <tr key={idx}>
                <td>{match.time}</td>
                <td>{match.home}</td>
                <td>{match.away}</td>
                <td>{match.tip}</td>
                <td>{match.ou}</td>
                <td>
                  {match.odds?.main?.join(" / ")}
                </td>
                <td>
                  {match.odds?.double?.join(" / ")}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default GermanyMatchCard;
