import React from 'react';
import { Col, Container, Image, Row } from 'react-bootstrap';

const MobileView = () => {
  return (
    <div style={{ backgroundColor: 'black', color: 'white', padding: '3rem 1rem' }}>
      <Container fluid>
        <Row className="gx-5 gy-4 align-items-center">
          {/* Image Section */}
          <Col md={9}>
            <Image
              src="https://media.gettyimages.com/id/2193883766/photo/aerial-view-of-a-library-study-area-shows-a-diverse-student-group.jpg?s=1024x1024&w=gi&k=20&c=ybHxW0mYd8OwR-DBI6B_jomz2rC5y9IqFqSr4K4cnp0="
              alt="Mobile Experience"
              fluid
              style={{
                width: '100%',
                height: 'auto',
                maxHeight: '550px',
                objectFit: 'cover',
                borderRadius: '10px',
              }}
            />
          </Col>

          {/* Text Section */}
          <Col md={3}>
            <p style={{ fontWeight: 'bold', fontSize: '1.25rem' }}>A Sleek Mobile Experience</p>
            <p style={{ fontSize: '0.95rem', lineHeight: '1.6' }}>
              The Brigade's main priority for the new website was having it powered by a CMS so that anyone on the team can make updates.
            </p>
          </Col>
        </Row>

        {/* Second Image Section */}
        <Row className="mt-5">
          <Col md={9}>
            <Image
              src="https://media.gettyimages.com/id/2193883766/photo/aerial-view-of-a-library-study-area-shows-a-diverse-student-group.jpg?s=1024x1024&w=gi&k=20&c=ybHxW0mYd8OwR-DBI6B_jomz2rC5y9IqFqSr4K4cnp0="
              alt="Secondary Mobile Image"
              fluid
              style={{
                width: '100%',
                height: 'auto',
                maxHeight: '550px',
                objectFit: 'cover',
                borderRadius: '10px',
              }}
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default MobileView;
