import React, { useState } from "react";
import { Container, Card, Button, ListGroup, Modal } from "react-bootstrap";
import { useCart } from "../pages/HomePage/CartContext";
import jsPDF from "jspdf";

const BetSlip = () => {
  const { cart, removePick, clearCart } = useCart();
  const [showModal, setShowModal] = useState(false);

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  // Function to generate PDF
  const generatePDF = () => {
    const doc = new jsPDF();

    // Add Logo
    const logoUrl = "https://via.placeholder.com/150x50?text=LOGO"; // replace with your real logo
    doc.addImage(logoUrl, "PNG", 80, 10, 50, 20);

    // Title
    doc.setFontSize(16);
    doc.text("Bet Slip Receipt", 20, 45);

    // Date
    const date = new Date().toLocaleString();
    doc.setFontSize(11);
    doc.text(`Generated on: ${date}`, 20, 55);

    // Add line separator
    doc.line(20, 60, 190, 60);

    // Bets
    doc.setFontSize(12);
    cart.forEach((pick, idx) => {
      doc.text(
        `${idx + 1}. ${pick.match}  |  ${pick.type}: ${pick.value}`,
        20,
        70 + idx * 10
      );
    });

    // Footer
    doc.setFontSize(10);
    doc.text("Thank you for using Fulltime-Predict.com", 20, 280);

    // Save PDF
    doc.save("BetSlip.pdf");
  };

  return (
    <Container className="my-4">
      <Card>
        <Card.Header className="fw-bold">
          My Bet Slip ({cart.length})
        </Card.Header>

        <ListGroup variant="flush">
          {cart.map((pick, idx) => (
            <ListGroup.Item
              key={idx}
              className="d-flex justify-content-between align-items-center"
            >
              <div>
                <strong>{pick.match}</strong> <br />
                {pick.type}: {pick.value}
              </div>
              <Button
                variant="danger"
                size="sm"
                onClick={() => removePick(idx)}
              >
                Remove
              </Button>
            </ListGroup.Item>
          ))}
        </ListGroup>

        {cart.length > 0 && (
          <Card.Footer className="d-flex justify-content-between">
            <Button variant="secondary" onClick={clearCart}>
              Clear
            </Button>
            <Button variant="success" onClick={handleShow}>
              Place Bet
            </Button>
          </Card.Footer>
        )}
      </Card>

      {/* Receipt Preview Modal */}
      <Modal show={showModal} onHide={handleClose} size="md" centered>
        <Modal.Header closeButton>
          <Modal.Title>Bet Slip Receipt</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Company Logo */}
          <div className="text-center mb-3">
            <img
              src="https://via.placeholder.com/180x60?text=LOGO"
              alt="Company Logo"
              style={{ maxWidth: "180px" }}
            />
          </div>

          {/* Date */}
          <p className="text-center text-muted">
            {new Date().toLocaleString()}
          </p>

          {/* Bet List */}
          <ListGroup>
            {cart.map((pick, idx) => (
              <ListGroup.Item key={idx}>
                <strong>{pick.match}</strong> <br />
                {pick.type}: {pick.value}
              </ListGroup.Item>
            ))}
          </ListGroup>

          <p className="text-center mt-3 text-success fw-bold">
            Thank you for using Fulltime-Predict.com
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="success" onClick={generatePDF}>
            Download Receipt
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default BetSlip;
