import React, { useState } from 'react';
import { Carousel, Container, Row, Col } from 'react-bootstrap';
import { ArrowLeft, ArrowRight } from 'react-bootstrap-icons';

const slides = [
  {
    image: 'https://images.unsplash.com/photo-1747582411588-f9b4acabe995?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHw5fHx8ZW58MHx8fHx8',
    title: 'The Beatles',
    subtitle: 'Case Study',
    description: 'We partnered with Spotify for the launch of music’s greatest catalogue on Christmas eve.',
  },
  {
    image: 'https://images.unsplash.com/photo-1749330513873-09ffa91b0aae?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxN3x8fGVufDB8fHx8fA%3D%3D',
    title: 'Another Project',
    subtitle: 'Case Study',
    description: 'Description of another great launch with deep insight into creative freedom.',
  },
];

const ImageCarousel = () => {
  const [index, setIndex] = useState(0);

  const handlePrev = () => setIndex(prev => (prev - 1 + slides.length) % slides.length);
  const handleNext = () => setIndex(prev => (prev + 1) % slides.length);

  return (
    <div style={{ backgroundColor: 'black', minHeight: '100vh', padding: '3rem 1rem', color: 'white' }}>
      <Container fluid>
        <Row className="gx-5 gy-4">
          {/* Image Carousel Section */}
          <Col md={9}>
            <Carousel
              fade
              activeIndex={index}
              onSelect={setIndex}
              indicators={false}
              controls={false}
              touch={false}
            >
              {slides.map((slide, idx) => (
                <Carousel.Item key={idx}>
                  <div
                    style={{
                      position: 'relative',
                      width: '100%',
                      height: '80vh',
                      backgroundImage: `url(${slide.image})`,
                      backgroundSize: 'cover',
                      backgroundPosition: 'center',
                      borderRadius: '8px',
                      overflow: 'hidden',
                    }}
                  >
                    {/* Overlay ONLY on the image */}
                    <div
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'rgba(0, 10, 90, 0.5)',
                        zIndex: 1,
                      }}
                    ></div>

                    {/* Text inside image */}
                    <div
                      className="h-100 d-flex flex-column justify-content-center align-items-center text-white text-center"
                      style={{
                        position: 'relative',
                        zIndex: 2,
                        padding: '1rem',
                      }}
                    >
                      <h2 className="mb-1">{slide.title}</h2>
                      <small className="mb-2">{slide.subtitle}</small>
                      <p style={{ maxWidth: '600px' }}>{slide.description}</p>
                    </div>
                  </div>
                </Carousel.Item>
              ))}
            </Carousel>
          </Col>

          {/* Controls + Static Text */}
          <Col md={3} className="d-flex flex-column justify-content-between">
            <div>
              <div className="d-flex align-items-center mb-3">
                <ArrowLeft style={{ cursor: 'pointer', fontSize:"13px" }} onClick={handlePrev} />
                <span style={{ margin: '0 0.5rem', fontSize:"13px"}}>
                  {index + 1}/{slides.length}
                </span>
                <ArrowRight style={{ cursor: 'pointer', fontSize:"13px" }} onClick={handleNext} />
              </div>
              <p style={{ fontWeight: 'bold' }}>Case study</p>
              <p style={{ fontSize: '0.9rem', lineHeight: '1.5' }}>
                The Brigade's main priority for the new website was having it powered by a CMS so that anyone on the team can make updates.
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImageCarousel;
