import React, { useState } from 'react';
import { Carousel, Row, Col, Container, Button,Accordion,Card,} from 'react-bootstrap';
import product from './Toothpaste Bits Fluoride Free/toothpasteBitsFluoride-Free.json';


const ProductDisplay = () => {
  const [activeIndex, setActiveIndex] = useState(0);
   const [activeVariant, setActiveVariant] = useState(0);
  //  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
 
  //  React.useEffect(() => {
  //    const handleResize = () => setIsMobile(window.innerWidth < 768);
  //    window.addEventListener('resize', handleResize);
  //    return () => window.removeEventListener('resize', handleResize);
  //  }, []);
 
   const currentVariant = product.variants[activeVariant];
 
   const handleCarouselSelect = (selectedIndex) => {
     setActiveIndex(selectedIndex);
   };
 
   const handleVariantSelect = (index) => {
     setActiveVariant(index);
     setActiveIndex(0); // Reset to first image when variant changes
   };
 
  return (
    <Container fluid className="my-5 border-bottom border-black">
      <Row>
        {/* Thumbnails - Left Column */}
        <Col md={1} className="pe-0  d-none d-md-block">
          <div className="d-flex flex-column h-100">
            {currentVariant.images.map((img, index) => (
              <div 
                key={index}
                className={`mb-2 ${activeIndex === index ? 'opacity-100 border border-black p-1' : 'opacity-50'}`}
                style={{ cursor: 'pointer', transition: 'opacity 0.3s' }}
                onClick={() => setActiveIndex(index)}
              >
                <img
                  src={img}
                  alt={`Thumbnail ${index + 1}`}
                  className="img-fluid p-1"
                  style={{ maxHeight: '600px', objectFit: 'contain' }}
                />
              </div>
            ))}
          </div>
        </Col>
        
        {/* Main Carousel - Middle Column */}
        <Col xs={12} md={6} className="ps-0 bg-initial">
          <Carousel 
          className='w-100 p-3 fs-3'
            activeIndex={activeIndex} 
            onSelect={handleCarouselSelect} 
            interval={null}
            variant="dark"
            controls={false}

            // controls={!isMobile}
            indicators={true}
          >
            {currentVariant.images.map((img, index) => (
              <Carousel.Item key={index}>
                <img
                  className="w-100"
                  src={img}
                  alt={`Product view ${index + 1}`}
                />
              </Carousel.Item>
            ))}
          </Carousel>
        </Col>
        
        {/* Product Details - Right Column */}
        <Col md={5} xs={12} className="mt-3 mt-md-0 px-md-4" style={{ 
          maxHeight: 'calc(100vh - 100px)',
          overflowY: 'scroll',
          position: 'sticky',
          top: '100px',
          scrollbarWidth: 'none',  // For Firefox
          msOverflowStyle: 'none'  // For IE 10+
        }}>
          {/* Hide scrollbar for WebKit browsers (Chrome, Safari) */}
          <style>
            {`
              ::-webkit-scrollbar {
                display: none;
              }
            `}
          </style>
        <Container fluid className='px-md-5 py-md-3'>
          <h2 className='display-6' >{product.name}</h2>
          
          <div className="d-flex align-items-center mb-3">
            <div className="text-warning fs-4">
              {'★★★★★'.slice(0, 5)}
              <span className="text-muted">{'★'.slice(0, 5 - Math.floor(product.rating))}</span>
            </div>
            <span className="ms-2">
              {product.rating} - {product.reviewCount.toLocaleString()} Reviews
            </span>
          </div>
          
          <p className="mb-4 pb-4" style={{ fontSize:"0.9rem"}}>{product.description}</p>
          
          <div className="d-flex flex-wrap gap-2 mb-4">
            {product.variants.map((variant, index) => (
              <Button 
                key={index}
                variant={activeVariant === index ? "primary" : "outline-secondary"}
                size="sm"
                className="text-capitalize rounded-0 px-3 fs-6"
                onClick={() => handleVariantSelect(index)}
              >
                {variant.name}
              </Button>
            ))}
          </div>
          
          {currentVariant.description && (
            <p className="text-muted mb-4">
              {currentVariant.description}
            </p>
          )}
          
          <div className="mt-3 mb-2">
            <small className="text-muted">
            A sexy, smoky sandalwood.
            </small>
          </div>

          <div className='border p-2 mb-2' style={{ fontSize:"0.8rem"}}>
            <p><span></span>{product.deliveryInfo}</p>
          <ul>
            {product.features.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
          </div>
                    <Button
                     variant="success"
                     size="lg"
                     style={{ borderRadius: '0', width: '100%', fontSize: '1rem' }}
                   >
                     ADD TO BAG /
                     <span className="text-decoration-line-through text-muted mx-2">$</span>
                     <span>$</span>
                   </Button>
                   
          
          <div className="mt-3">
            <small className="text-muted">
              Satisfaction Guaranteed •  Free Shipping Over $32 • Secure Check Out
            </small>
          </div>
          <hr className='text-dark' />

          <div className='mt-3'>
          <p>{product.incentiveinfo}</p>
          <ul className='' style={{ listStyle:"none", fontSize:"0.9rem"}}>
            {product.items.map((items, index) => (
              <li key={index}>{items}</li>
            ))}
          </ul>
          </div>

          <Accordion defaultActiveKey="0" className="mt-3">
  {/* Instructions Dropdown */}
  <Accordion.Item eventKey="0">
    <Accordion.Header className="text-uppercase">
      Instructions
    </Accordion.Header>
    <Accordion.Body>
    <p>{product.info}</p>
      </Accordion.Body>
  </Accordion.Item>

  {/* Clean Ingredients Dropdown */}
  <Accordion.Item eventKey="1">
    <Accordion.Header className="text-uppercase">
      Clean Ingredients
    </Accordion.Header>
    <Accordion.Body>
      <Card.Text className="small">
        Xylitol, Calcium Carbonate, Sodium Bicarbonate, 
        Sodium Cocoyl Glutamate, Natural Flavor, 
        Mentha Piperita (Peppermint) Oil, 
        Mentha Arvensis (Mint) Oil, 
        Stevia Rebaudiana Leaf Extract.
      </Card.Text>
    </Accordion.Body>
  </Accordion.Item>
</Accordion>
          </Container>
        </Col>
      </Row>
    </Container>
  );
};

export default ProductDisplay;
