import React, { useState, useEffect, useMemo } from "react";

const Animationtext = () => {
  const titles = useMemo(
    () => ["Executive Landmarks", "Innovative Architecture", "Modern Business Spaces"],
    []
  );

  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);
  const [subIndex, setSubIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);

  useEffect(() => {
    if (subIndex === titles[index].length + 1 && !deleting) {
      setTimeout(() => setDeleting(true), 1000);
      return;
    }

    if (subIndex === 0 && deleting) {
      setDeleting(false);
      setIndex((prev) => (prev + 1) % titles.length);
      return;
    }

    const timeout = setTimeout(() => {
      setSubIndex((prev) => (deleting ? prev - 1 : prev + 1));
      setText(titles[index].substring(0, subIndex));
    }, deleting ? 60 : 120);

    return () => clearTimeout(timeout);
  }, [subIndex, index, deleting, titles]);

  return (
    <h1
      style={{
        fontWeight: 600,
        color: "#661414",
        textAlign: "center",
        padding: "2rem 0",
        minHeight: "2rem",
        whiteSpace: "nowrap",
        overflow: "hidden",
        borderRight: "2px solid #661414",
        width: "fit-content",
        margin: "0 auto",
      }}
    >
      <span
        style={{
          display: "inline-block",
          transition: "opacity 0.3s ease-in-out, transform 0.3s ease-in-out",
          opacity: 1,
          transform: deleting ? "translateY(-2px)" : "translateY(0)",
        }}
      >
        {text}
      </span>
    </h1>
  );
};

export default Animationtext;
