import React from 'react';
import { Carousel, Container, Row, Col } from 'react-bootstrap';

const slides = [
  {
    image: 'https://images.unsplash.com/photo-1747582411588-f9b4acabe995?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHw5fHx8ZW58MHx8fHx8',
    title: 'The Beatles',
    subtitle: 'Case Study',
    description: 'We partnered with Spotify for the launch of music’s greatest catalogue on Christmas eve.',
  },
  {
    image: 'https://images.unsplash.com/photo-1749330513873-09ffa91b0aae?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxN3x8fGVufDB8fHx8fA%3D%3D',
    title: 'Another Project',
    subtitle: 'Case Study',
    description: 'Description of another great launch with deep insight into creative freedom.',
  },
  // Add more slides as needed
];

const ImageCarousel = () => {
  return (
    <Container fluid className="p-0 position-relative" style={{ backgroundColor: 'black', minHeight: '100vh' }}>
      <Col md={9}>
      <Carousel fade controls indicators={false}>
        {slides.map((slide, idx) => (
          <Carousel.Item key={idx}>
            <div
              style={{
                backgroundImage: `url(${slide.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                width: '100%',
                height:"50vh",
                position: 'relative',
              }}
            >
              {/* Overlay */}
              <div
                style={{
                  backgroundColor: 'rgba(0, 10, 90, 0.7)',
                  position: 'absolute',
                  top: 0,
                  bottom: 0,
                  left: 0,
                  right: 0,
                  zIndex: 1,
                }}
              />
              {/* Text */}
              <Container
                className="h-100 d-flex flex-column justify-content-center align-items-center text-white text-center"
                style={{ position: 'relative', zIndex: 2 }}
              >
                <h2 className="mb-1">{slide.title}</h2>
                <small className="mb-3">{slide.subtitle}</small>
                <p className="px-3" style={{ maxWidth: '600px' }}>
                  {slide.description}
                </p>
              </Container>
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
      </Col>
      <Col md={2}>
      </Col>
      
    </Container>
  );
};

export default ImageCarousel;
