import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Badge, Button } from "react-bootstrap";
import GermanyMatchForm from "../../components/GermanyMatchForm";

const API = "http://localhost:5000/germany";

const GermanyMatchCard = () => {
  const [league, setLeague] = useState(null);

  const fetchData = () => {
    fetch(API)
      .then((res) => res.json())
      .then((data) => setLeague(data.league));
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (!league) return <p>Loading...</p>;

  return (
    <Container>
      <GermanyMatchForm onAdded={fetchData} />

      <Card>
        <Card.Header>
          <img src={league.flag} alt="flag" /> {league.name}
          <Badge bg="dark">{league.matches.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {league.matches.map((m, i) => (
            <Row key={i} className="mb-2 border-bottom">
              <Col>{m.time}</Col>
              <Col>{m.home} vs {m.away}</Col>
              <Col>{m.tip}</Col>
              <Col>{m.ou}</Col>
            </Row>
          ))}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default GermanyMatchCard;
