import React from "react";
import { Container, Row, Col, Button, Badge, Image } from "react-bootstrap";

const BetterTogether = () => {
  return (
    <Container fluid className="bg-light py-5 border-top border-bottom border-black">
      <Container>
        <Row className="align-items-center px-3 py-5">

        <Col md={6} className="order-sm-2 order-2 ">
            <Row className="g-2">
              {[
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-2.jpg?v=1700114048&width=729",
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-5.jpg?v=1700114048&width=1032",
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-4.jpg?v=1700114048&width=1032",
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-3.jpg?v=1700114048&width=726",
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-6.jpg?v=1700114048&width=1032",
                "https://bitetoothpastebits.com/cdn/shop/files/about-us-event-5.jpg?v=1700114048&width=1032",
              ].map((src, index) => (
                <Col xs={4} key={index}>
                  <Image src={src} alt={`Image ${index + 1}`} fluid className="rounded-o" />
                </Col>
              ))}
            </Row>
          </Col>

          {/* Text Section */}
          <Col md={6} className="mb-4 mb-md-0 ">
            <div className="px-2 px-md-4">
              <Badge bg="success" className="mb-3 px-4 fs-6 py-2 text-uppercase rounded-0">
                Today
              </Badge>
              <h2 className="fw-bold display-5 mb-3">Better together.</h2>
              <p className="lead">
                Because of you, <strong>250,000 lbs of plastic</strong> was kept out of landfills and oceans.
              </p>
              <Button variant="dark" className="mt-4 px-4 py-2 text-uppercase rounded-0">
                Shop with purpose
              </Button>
            </div>
          </Col>

          {/* Image Grid */}
         
        </Row>
      </Container>
    </Container>
  );
};

export default BetterTogether;
