import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const timelineData = [
  {
    year: "In 1968,",
    text: `Mr. Ishwardas Mahtani and his two sons founded what is today known as the Churchgate Group. However, we started as a textile manufacturer and large-scale importer and distributor of essential commodities.`,
  },
  {
    year: "In the 1970s,",
    text: `We expanded into industries like deep-sea fishing and shrimping, beer production, and glass manufacturing.`,
  },
  {
    year: "In the 1980s,",
    text: `We ventured into banking and finance, setting up Chartered Bank PLC in 1988 and Regent Bank PLC.`,
  },
];

const TimelineSection = () => (
  <section
    style={{
      backgroundImage:  "url('https://images.unsplash.com/photo-1603791452906-cdd24585d101?ixlib=rb-4.0.3&auto=format&fit=crop&w=1470&q=80')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      position: "relative",
      color: "#fff",
    }}
  >
    <div
      style={{
          backgroundColor: "rgba(0,0,0,0.65)",
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 1,
      }}
    />
    <Container className="py-5 position-relative" style={{ zIndex: 2 }}>
      <h2 className="text-center mb-5 fs-1 fw-bold">Our History</h2>
      <h3 className="text-center mb-5 fs-2">Over 5 Decades of Excellence</h3>

      <div style={{ position: "relative" }}>
        {/* Vertical line */}
        <div
          style={{
            position: "absolute",
            left: "50%",
            top: 0,
            bottom: 0,
            width: "4px",
            backgroundColor: "#fff",
            transform: "translateX(-50%)",
            zIndex: 1,
          }}
        />

        {timelineData.map(({ year, text }, index) => {
          const isLeft = index % 2 === 0;
          return (
            <Row className="mb-5 align-items-center" key={index}>
              <Col md={5} className={isLeft ? "text-end" : "order-md-2 text-start"}>
                <h4 className="fw-bold">{year}</h4>
                <p>{text}</p>
              </Col>

              <Col md={2} className="d-flex justify-content-center align-items-center position-relative">
                {/* Red dot */}
                <div
                  style={{
                    width: 20,
                    height: 20,
                    borderRadius: "50%",
                    backgroundColor: "red",
                    border: "3px solid white",
                    zIndex: 2,
                  }}
                />
              </Col>

              <Col md={5} className={isLeft ? "d-none d-md-block" : "order-md-1 d-none d-md-block"}></Col>
            </Row>
          );
        })}
      </div>
    </Container>
  </section>
);

export default TimelineSection;
