import React, { useState } from 'react';
import { Row, Col, Button } from 'react-bootstrap';

const FeatureCards = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  const features = [
    {
      id: 1,
      title: 'BETTER-FOR-YOUR-FORMULATION',
      icon: '+',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/2024.07.01_homepage_howto_step1_desktop_1_-optimized.jpg?v=1719614439&width=936',
      content: `We're proud to be a Certified B Corp and 100% carbon neutral. By using only recyclable, biodegradable or compostable materials, we are able to replace products that would otherwise end up in landfills and oceans. Since starting, we've helped divert over 250,000lbs of plastic waste from our oceans and landfills.`,
   
    },
    {
      id: 2,
      title: 'HELLO, BETTER HYGEINE',
      icon: '—',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/2024.07.01_homepage_howto_step2_desktop_1_-optimized.jpg?v=1719614439&width=936',
      content: `We're proud to be a Certified B Corp and 100% carbon neutral. By using only recyclable, biodegradable or compostable materials, we are able to replace products that would otherwise end up in landfills and oceans. Since starting, we've helped divert over 250,000lbs of plastic waste from our oceans and landfills.`,
    },
    {
      id: 3,
      title: 'SINK,SUIT CASE AND SEA-FREINDLY',
      icon: '+',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/2024.07.01_homepage_howto_step3_desktop_1_-optimized.jpg?v=1719614439&width=936',
      content:  `We're proud to be a Certified B Corp and 100% carbon neutral. By using only recyclable, biodegradable or compostable materials, we are able to replace products that would otherwise end up in landfills and oceans. Since starting, we've helped divert over 250,000lbs of plastic waste from our oceans and landfills.`,
    },
  ];

  return (
    <section className="p-5 bg-white border-bottom border-black">
      <Row className="g-4">
        {features.map((feature) => (
          <Col key={feature.id} xs={12} md={4}>
            <div
              onMouseEnter={() => setHoveredCard(feature.id)}
              onMouseLeave={() => setHoveredCard(null)}
              className="position-relative d-flex flex-column justify-content-end"
              style={{
                height: '450px',
                backgroundImage: `url(${feature.img})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                color: hoveredCard === feature.id && feature.content ? 'white' : 'black',
                transition: 'color 0.3s ease',
              }}
            >
              {/* Dark overlay on hover */}
              {feature.content && (
                <div
                  className="position-absolute top-0 start-0 w-100 h-100"
                  style={{
                    backgroundColor: hoveredCard === feature.id ? 'rgba(0, 0, 0, 0.6)' : 'transparent',
                    transition: 'background-color 0.3s ease',
                    zIndex: 1,
                  }}
                />
              )}

              {/* Content text on hover */}
              {feature.content && hoveredCard === feature.id && (
                <div
                  className="position-absolute top-0 p-3 text-white"
                  style={{ zIndex: 2, fontSize: '1.0rem' }}
                >
                  {feature.content}
                </div>
              )}

              {/* Icon + Title always shown */}
              <div
                className="d-flex align-items-center px-3 py-2"
                style={{ zIndex: 2 }}
              >
                
                <Button
                  variant="outline-dark"
                  className="border rounded-0 text-start p-2"
                  style={{
                    background: 'rgba(255, 255, 255,0.5)',
                    color: hoveredCard === feature.id && feature.content ? 'grey' : 'inherit',
                    fontWeight: '500',
                    fontSize: '1rem',
                    transition: 'color 0.3s ease',
                  }}
                ><span className="me-2" style={{ fontSize: '1.5rem' }}>
                {feature.icon}
              </span>
                  {feature.title}
                </Button>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </section>
  );
};

export default FeatureCards;
