// src/components/LaligaMatchForm.jsx
import React, { useState } from "react";

const LaligaMatchForm = ({ onAdded }) => {
  const [form, setForm] = useState({
    time: "",
    home: "",
    away: "",
    h2h: ["", "", "", "", "", ""],
    form: ["", "", "", "", "", ""],
    odds: {
      main: ["", "", ""], // 1, X, 2
      double: ["", "", ""], // 1X, 12, X2
    },
    tip: "",
    stat: { home: "", away: "" },
    ou: "",
  });

  // Handle normal input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle arrays (h2h, form, odds, etc.)
  const handleArrayChange = (e, field, index, subfield) => {
    if (field === "odds") {
      const updated = { ...form.odds };
      updated[subfield][index] = e.target.value;
      setForm({ ...form, odds: updated });
    } else {
      const updated = [...form[field]];
      updated[index] = e.target.value;
      setForm({ ...form, [field]: updated });
    }
  };

  // Handle stats
  const handleStatChange = (e, team) => {
    setForm({
      ...form,
      stat: { ...form.stat, [team]: e.target.value },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:5000/laliga", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({
      time: "",
      home: "",
      away: "",
      h2h: ["", "", "", "", "", ""],
      form: ["", "", "", "", "", ""],
      odds: { main: ["", "", ""], double: ["", "", ""] },
      tip: "",
      stat: { home: "", away: "" },
      ou: "",
    });
    if (onAdded) onAdded();
  };

  return (
    <form onSubmit={handleSubmit} className="mb-3 p-3 border rounded">
      <h5>Add La Liga Match</h5>

      <input className="form-control mb-2" placeholder="Time" name="time" value={form.time} onChange={handleChange} required />
      <input className="form-control mb-2" placeholder="Home Team" name="home" value={form.home} onChange={handleChange} required />
      <input className="form-control mb-2" placeholder="Away Team" name="away" value={form.away} onChange={handleChange} required />

      <h6>H2H (last 6)</h6>
      {form.h2h.map((val, i) => (
        <input key={i} className="form-control mb-2" placeholder={`H2H ${i + 1}`} value={val} onChange={(e) => handleArrayChange(e, "h2h", i)} />
      ))}

      <h6>Form (last 6)</h6>
      {form.form.map((val, i) => (
        <input key={i} className="form-control mb-2" placeholder={`Form ${i + 1}`} value={val} onChange={(e) => handleArrayChange(e, "form", i)} />
      ))}

      <h6>Odds (Main: 1, X, 2)</h6>
      {form.odds.main.map((val, i) => (
        <input key={i} className="form-control mb-2" placeholder={`Main ${["1", "X", "2"][i]}`} value={val} onChange={(e) => handleArrayChange(e, "odds", i, "main")} />
      ))}

      <h6>Odds (Double Chance: 1X, 12, X2)</h6>
      {form.odds.double.map((val, i) => (
        <input key={i} className="form-control mb-2" placeholder={`Double ${["1X", "12", "X2"][i]}`} value={val} onChange={(e) => handleArrayChange(e, "odds", i, "double")} />
      ))}

      <input className="form-control mb-2" placeholder="Tip (e.g. 1x)" name="tip" value={form.tip} onChange={handleChange} required />

      <h6>Stats</h6>
      <input className="form-control mb-2" placeholder="Home Stat (%)" value={form.stat.home} onChange={(e) => handleStatChange(e, "home")} />
      <input className="form-control mb-2" placeholder="Away Stat (%)" value={form.stat.away} onChange={(e) => handleStatChange(e, "away")} />

      <input className="form-control mb-2" placeholder="O/U (e.g. un3.5)" name="ou" value={form.ou} onChange={handleChange} required />

      <button className="btn btn-primary w-100" type="submit">
        Add Match
      </button>
    </form>
  );
};

export default LaligaMatchForm;
