// src/components/LaligaMatchCard.jsx
import React, { useEffect, useState } from "react";
import { Card, Badge, Button } from "react-bootstrap";

  
  const API = "http://localhost:5000/laliga";
  
 const LaligaMatchCard = () => {
    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);
  
    // Fetch matches
    const fetchMatches = () => {
      setLoading(true);
      fetch(API)
        .then((res) => res.json())
        .then((data) => {
          if (Array.isArray(data)) {
            setMatches(data);
          } else if (Array.isArray(data.laliga)) {
            setMatches(data.laliga);
          } else {
            setMatches([]);
          }
          setLoading(false);
        })
        .catch((err) => {
          console.error("Error fetching matches:", err);
          setMatches([]);
          setLoading(false);
        });
    };
  
    useEffect(() => {
      fetchMatches();
    }, []);
    
  return (
    
    <div className="my-4">

       {/* Add Match Form */}

      {loading ? (
        <p>Loading...</p>
      ) : matches.length === 0 ? (
        <p>No matches found.</p>
      ) : (
      <Card
        style={{
          borderRadius: "12px",
          overflow: "hidden",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        {/* Header */}
        <Card.Header
          style={{
            background: "linear-gradient(90deg, #800000, #b71c1c)",
            color: "white",
            fontWeight: "bold",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <div className="d-flex align-items-center gap-2">
            <img
              src="https://flagcdn.com/w20/es.png"
              alt="flag"
              style={{ width: "24px", height: "16px" }}
            />
            Spanish La Liga
          </div>
          <Badge bg="dark">{matches.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {matches.map((m) => (
            <Card
              key={m.id}
              className="mb-3 p-3"
              style={{
                borderRadius: "10px",
                border: "1px solid #eee",
                background: "#fafafa",
              }}
            >
              <div className="d-flex justify-content-between align-items-center">
                <strong>{m.time}</strong>
                <Button size="sm" variant="primary">
                  {m.tip}
                </Button>
              </div>
              <div className="text-center my-2">
                <div>{m.home}</div>
                <div style={{ fontWeight: "bold" }}>VS</div>
                <div>{m.away}</div>
              </div>
              <div
                style={{
                  backgroundColor: "#f3e5f5",
                  color: "purple",
                  fontSize: "0.75rem",
                  borderRadius: "6px",
                  padding: "4px",
                  textAlign: "center",
                }}
              >
                O/U {m.ou}
              </div>
            </Card>
          ))}
        </Card.Body>
      </Card>
      )}
    </div>
  );
};

export default LaligaMatchCard;
