import React, { useState } from 'react';
import { Carousel, Container, Row, Col} from 'react-bootstrap';

const slides = [
  {
    image: 'https://images.unsplash.com/photo-1747582411588-f9b4acabe995?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHw5fHx8ZW58MHx8fHx8',
    title: 'The Beatles',
    subtitle: 'Case Study',
    description: 'We partnered with Spotify for the launch of music’s greatest catalogue on Christmas eve.',
  },
  {
    image: 'https://images.unsplash.com/photo-1749330513873-09ffa91b0aae?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxN3x8fGVufDB8fHx8fA%3D%3D',
    title: 'Another Project',
    subtitle: 'Case Study',
    description: 'Description of another great launch with deep insight into creative freedom.',
  },
  // Add more slides as needed
];

const ImageCarousel = () => {
    const [index, setIndex] = useState(0);

  return (
    <div className="p-5" style={{ backgroundColor: 'black', minHeight: '100vh' }}>
 <div className="position-absolute top-0 end-0 p-3 d-flex align-items-center">
         <Carousel.Control 
          direction="prev" 
          onClickHandler={() => setIndex(prev => (prev - 1 + slides.length) % slides.length)}
          className="position-static border-0 bg-transparent p-0 me-2"
        />
        
        <span className="mx-2">
          {index + 1}/{slides.length}
        </span>
        
        <Carousel.Control 
          direction="next"
          onClickHandler={() => setIndex(prev => (prev + 1) % slides.length)}
          className="position-static border-0 bg-transparent p-0 ms-2"
        />
      </div>
      
         {/* Numbered Controls - Top Position */}
      

      <Container>
      <Row>
      <Col md={9}>
      <Carousel fade 
         activeIndex={index}
        onSelect={setIndex}
        indicators={false}
        controls={false}
        touch={false}
        className="min-vh-100">
{slides.map((slide, idx) => (
          <Carousel.Item key={idx} className="d-flex align-items-center">
            <div className="w-100 p-5" style={{ maxWidth: '800px' }}>
              <h1 className="display-3 mb-4">{slide.title}</h1>
              <p className="fs-4 mb-4">{slide.content}</p>
              <p className="fs-4 mb-5">{slide.summary}</p>
              <p className="fs-5 text-muted">{slide.quote}</p>
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
      </Col>
      <Col md={2}>
      <p className='fw-bold'>Work showcase</p>
         <p className="mb-0">
              The Brigade's main priority for the new website was having it powered by a CMS so that anyone on the team can make updates.
            </p>
      </Col>
      </Row>
      </Container>
    </div>
  );
};

export default ImageCarousel;
