import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CGT1HeroSplitSlider = () => {
  const images = [
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x650.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-1000x650.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/wtc.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-11-1300x650.jpg'
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [overlayWidth, setOverlayWidth] = useState('60%');

  useEffect(() => {
    const handleResize = () => {
      setOverlayWidth(window.innerWidth < 768 ? '100%' : '60%');
    };

    handleResize(); // initial check
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prev => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  const leftColStyle = {
    backgroundImage: `url(${images[currentIndex]})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    transition: 'background-image 1s ease-in-out',
    minHeight: '100vh',
    position: 'relative',
    padding: 0
  };

  const overlayStyle = {
    backgroundColor: 'rgba(78, 78, 78, 0.4)',
    minHeight: '100vh',
    width: overlayWidth,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '2rem'
  };

  return (
    <Container fluid className="p-0">
      <Row className="g-0">
        {/* LEFT COLUMN with background and overlay */}
        <Col data-aos="fade-left" md={8} style={leftColStyle} >
          <div style={overlayStyle}>
            <h1 className="text-white text-md-start fw-medium lh-1 display-5">
              Building Iconic<br />
              Urban<br />
              Developments That<br />
              Define Nigeria
            </h1>
          </div>
        </Col>

        {/* RIGHT COLUMN - static red content */}
        <Col data-aos="flip-left" md={4} className="d-flex align-items-center fs-5" style={{ backgroundColor: '#981c20', padding: '3rem' }}>
          <div className="text-white" >
            <p>
              The Churchgate Group is focused on developing landmark destinations and creating a
              positive “sense of place” in whole communities.
            </p>
            <p>
              By paying attention to the needs of our end users — such as Fortune 500 businesses
              to aspiring families and luxury retailers — we create quality environments that not
              only exceed the needs of our demanding clientele but also enhance their style of
              living in the burgeoning cities of Lagos and Abuja.
            </p>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default CGT1HeroSplitSlider;
