import React, { useState } from 'react';
import { Button, Image } from 'react-bootstrap';

const ProductCard = ({ category, addToCart }) => {
  const [hovered, setHovered] = useState(false);

  return (
    <div
      className="position-relative overflow-hidden"
      style={{ height: '400px', cursor: 'pointer', backgroundColor: '#f8f9fa' }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      key={category.id}
    >
      <Image
        src={category.img}
        alt={category.title}
        className="w-100 h-100 object-fit-cover"
        style={{
          transition: 'transform 0.4s ease',
          transform: hovered ? 'scale(1.05)' : 'scale(1)',
        }}
      />
      <div className="position-absolute top-0 start-0 p-3">
        <h6 className="text-dark fs-4 fw-light m-0 text-uppercase">{category.title}</h6>
      </div>

      {/* Slide Up Panel */}
      <div
        className="position-absolute bottom-0 start-0 end-0 text-center"
        style={{
          background: '#fff',
          transform: hovered ? 'translateY(0)' : 'translateY(100%)',
          transition: 'transform 0.4s ease',
          zIndex: hovered ? 2 : 0,
        }}
      >
        <div className="d-flex flex-column">
          <div className="d-flex pt-2 px-2 justify-content-between border">
            <p className="badge text-black fw-small border bg-success">{category.badgeText}</p>
            <p className="badge text-muted">{category.flavor}</p>
          </div>
          <div className="d-flex pt-2 px-2 justify-content-between border">
            <p className="badge text-black fw-small">Subscribe and Save</p>
            <p className="badge text-light bg-dark rounded-0">Save 35%</p>
          </div>
          <Button
            variant="success"
            size="lg"
            style={{ borderRadius: '0', width: '100%', fontSize: '1rem' }}
             onClick={() => addToCart(category)}
          >
            ADD TO BAG /
            <span className="text-decoration-line-through text-muted mx-2">${category.oldPrice}</span>
            <span>${category.price}</span>
          </Button>
        </div>
      </div>

      {/* Main Button */}
      {!hovered && (
        <Button
          variant="dark"
          size="lg"
          style={{
            borderRadius: '0',
            width: '100%',
            fontSize: '1rem',
            position: 'absolute',
            bottom: 0,
            left: 0,
            zIndex: 1,
          }}
        >
          ADD TO BAG
        </Button>
      )}
    </div>
  );
};

export default ProductCard;
