import React from "react";
import { Container, Row, Col, Button, Card, Badge } from "react-bootstrap";
import { Clock, Trophy, BarChart, Lightning, GraphUp, Dice5 } from "react-bootstrap-icons";
import { useCart } from "./CartContext";   // ðŸ‘ˆ import

const OtherMatchCard = () => {
  const { addPick } = useCart(); // ðŸ‘ˆ use Cart functions

  const league = {
    name: "OtherLeagues",
    flag: "https://flagcdn.com/w20/gb.png",
    matches: [
      {
        time: "04:30",
        home: "Leeds",
        away: "Everton",
        h2h: [0, 0, 0, 0, 0, 0],
        form: ["D", "D", "D", "D", "D", "D"],
        odds: {
          main: [2.25,"1", 3.40, 3.20],
          double: [1.36, 1.30, 1.62],
        },
        tip: "1x",
        stat: { home: "23%", away: "77%" },
        ou: "un3.5",
      },
      {
        time: "04:30",
        home: "Leeds",
        away: "Manchester United",
        h2h: [0, 0, 0, 0, 0, 0],
        form: ["D", "D", "D", "D", "D", "D"],
        odds: {
          main: [2.25, 3.40, 3.20],
          double: [1.36, 1.30, 1.62],
        },
        tip: "2",
        stat: { home: "23%", away: "77%" },
        ou: "ov2.5",
      }
    ],
  };

  const headers = [
    { label: "Time", icon: <Clock /> },
    { label: "Fixture", icon: <Trophy /> },
    { label: "H2H", icon: <BarChart /> },
    { label: "Form", icon: <GraphUp /> },
    { label: "Odds", icon: <Dice5 /> },
    { label: "Tip", icon: <Lightning /> },
    { label: "Stat", icon: <BarChart /> },
    { label: "O/U", icon: <GraphUp /> },
  ];

  const mobileVisible = ["Time", "Fixture", "Tip", "O/U"];

  return (
    <Container fluid className="my-0 mb-4">
      <Card style={{ borderRadius: "12px", overflow: "hidden", boxShadow: "0 4px 8px rgba(0,0,0,0.1)" }}>
        <Card.Header
          style={{
            background: "linear-gradient(180deg, #0a1c54, #0b2a7a)",
            color: "white",
            fontWeight: "bold",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "1.9rem 1rem",
          }}
        >
          <div className="d-flex align-items-center gap-2">
            <img src={league.flag} alt="flag" style={{ width: "24px", height: "16px" }} />
            {league.name}
          </div>
          <Badge bg="dark">{league.matches.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {/* Table Header */}
          <Row
            className="text-center fw-bold mb-2"
            style={{ fontSize: "0.8rem", borderBottom: "1px solid #eee" }}
          >
            {headers.map((h, i) => (
              <Col
                xs={mobileVisible.includes(h.label) ? 3 : 0}
                md={i === 3 ? 5 : 1}
                key={i}
                className={
                  mobileVisible.includes(h.label)
                    ? " pb-2"
                    : "d-none d-md-block pb-2"
                }
              >
                {h.icon} {h.label}
              </Col>
            ))}
          </Row>

          {/* Matches */}
          {league.matches.map((match, idx) => (
            <Row
              key={idx}
              className="text-center align-items-center py-2 fw-bold"
              style={{ fontSize: "0.75rem", borderBottom: "1px solid #f1f1f1" }}
            >
              {/* Time */}
              <Col xs={3} md={1}>
                <strong>{match.time}</strong>
              </Col>

              {/* Fixture */}
              <Col xs={3} md={2}>
                <div>{match.home}</div>
                <div style={{ fontWeight: "bold" }}>VS</div>
                <div>{match.away}</div>
              </Col>

              
                  {/* H2H (desktop only) */}
                  <Col xs={0} md={2} className="d-none d-md-block">
                    <div className="d-flex justify-content-center gap-1">
                      {match.h2h.map((num, i) => (
                        <div
                          key={i}
                          style={{
                            width: "20px",
                            height: "20px",
                            backgroundColor: "#e3f2fd",
                            fontSize: "0.7rem",
                            borderRadius: "3px",
                          }}
                        >
                          {num}
                        </div>
                      ))}
                    </div>
                  </Col>
              
                  {/* Form (desktop only) */}
                  <Col xs={0} md={2} className="d-none d-md-block">
                    <div className="d-flex justify-content-center gap-1">
                      {match.form.map((f, i) => (
                        <span
                          key={i}
                          style={{
                            color: f === "W" ? "green" : f === "L" ? "red" : "#999",
                            fontWeight: "bold",
                          }}
                        >
                          {f}
                        </span>
                      ))}
                    </div>
                  </Col>
              
                  {/* Odds (desktop only) */}
                  <Col xs={0} md={2} className="d-none d-md-block">
                    <div className="d-flex flex-column">
                      <div className="d-flex justify-content-center gap-2">
                        {match.odds.main.map((odd, i) => (
                          <span key={i}>{odd}</span>
                        ))}
                      </div>
                      <div className="d-flex justify-content-center gap-2">
                        {match.odds.double.map((odd, i) => (
                          <span key={i}>{odd}</span>
                        ))}
                      </div>
                    </div>
                  </Col>

              {/* Tip (clickable) */}
              <Col xs={3} md={1}>
                <Button
                  className="fw-bold"
                  size="sm"
                  variant="primary"
                  style={{ fontSize: "0.6rem" }}
                  onClick={() =>
                    addPick({
                      type: "Tip",
                      match: `${match.home} vs ${match.away}`,
                      value: match.tip,
                    })
                  }
                >
                  {match.tip}
                </Button>
              </Col>

              
                  {/* Stat (desktop only) */}
                  <Col xs={0} md={1} className="d-none d-md-block">
                    <div
                      style={{
                        backgroundColor: "#e8f5e9",
                        color: "green",
                        fontSize: "0.70rem",
                        borderRadius: "6px",
                        padding: "3px",
                      }}
                    >
                      Home {match.stat.home}
                    </div>
                    <div
                      style={{
                        backgroundColor: "#e8f5e9",
                        color: "green",
                        fontSize: "0.75rem",
                        borderRadius: "6px",
                        padding: "4px",
                      }}
                    >
                      Away {match.stat.away}
                    </div>
                  </Col>

              {/* O/U (clickable) */}
              <Col xs={3} md={1}>
                <div
                  onClick={() =>
                    addPick({
                      type: "O/U",
                      match: `${match.home} vs ${match.away}`,
                      value: match.ou,
                    })
                  }
                  style={{
                    backgroundColor: "#f3e5f5",
                    color: "purple",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                    cursor: "pointer",
                  }}
                >
                  O/U <br /> {match.ou}
                </div>
              </Col>
            </Row>
          ))}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default OtherMatchCard;
