import React from 'react';
import { Carousel, Container } from 'react-bootstrap';

// Group logos in sets of 4–5 per slide
const logoSets = [
  [
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-3-2-pymvqy9357wj65rqvo0pwcrislywjikzghbavx1zw2.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-3-pymvqxb8ydv8ujt415m3bv02783jbth94cnten3e2a.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-1-pymvqwderjtyixuh6n7grd8llu8644dis80bxd4s8i.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-1-1-pymvqxb8ydv8ujt415m3bv02783jbth94cnten3e2a.png',
  ],
  [
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-3-2-pymvqy9357wj65rqvo0pwcrislywjikzghbavx1zw2.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-2-pymvqxb8ydv8ujt415m3bv02783jbth94cnten3e2a.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-4-pymvqxb8ydv8ujt415m3bv02783jbth94cnten3e2a.png',
    'https://churchgate.com/wp-content/uploads/elementor/thumbs/IMG_3433-2-1-pymvqy9357wj65rqvo0pwcrislywjikzghbavx1zw2.png',
  ],
  [
    '/logos/logo9.png',
    '/logos/logo10.png',
    '/logos/logo11.png',
    '/logos/logo12.png',
  ],
];

const WCULogos = () => {
  return (
    <Container className="py-3">
      <Carousel indicators={true} controls={true} interval={3000}>
        {logoSets.map((set, index) => (
          <Carousel.Item key={index}>
            <div className="d-flex justify-content-around align-items-center flex-wrap gap-4">
              {set.map((logo, i) => (
                <img
                  key={i}
                  src={logo}
                  alt={`Company Logo ${i + 1}`}
                  style={{
                    height: '30px',
                    objectFit: 'contain',
                  }}
                />
              ))}
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
};

export default WCULogos;
