import React, { useEffect, useState } from "react";
import GermanyMatchForm from "../components/GermanyMatchForm";

const AdminDashboard = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/germany")
      .then((res) => res.json())
      .then((data) => setMatches(data));
  }, []);

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/matches/${id}`, { method: "DELETE" });
    setMatches(matches.filter((m) => m.id !== id));
  };

  const handleAdded = () => {
    fetch("http://localhost:5000/germany")
      .then((res) => res.json())
      .then((data) => setMatches(data));
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl mb-4">Admin Dashboard</h2>
      <GermanyMatchForm onAdded={handleAdded} />

      <ul>
        {matches.map((match) => (
          <li key={match.id} className="flex justify-between border p-2 mb-2">
            {match.home} vs {match.away} ({match.time})
            <button
              onClick={() => handleDelete(match.id)}
              className="bg-red-500 text-white px-3 py-1 rounded"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;

