import React from 'react'
import { Button, Card, Image } from 'react-bootstrap';

const UpsellItems = () => {
    
const upsellItems = [
  {
    name: 'The Duo',
    price: 20,
    original: 24,
    image: 'https://via.placeholder.com/60',
  },
  {
    name: 'The Travel Kit',
    price: 40,
    original: 52,
    image: 'https://via.placeholder.com/60',
  },
];
  return (
    <div>
 {/* Left Column - Upsell & Offers */}
          {/* Upsell Section */}
          {upsellItems.map((item, idx) => (
            <Card key={idx} className="mb-3">
              <Card.Body className="d-flex align-items-center justify-content-between">
                <div className="d-flex align-items-center">
                  <Image src={item.image} width={60} className="me-3" />
                  <div>
                    <div>{item.name}</div>
                    <div>
                      <strong>${item.price.toFixed(2)}</strong>{' '}
                      <del className="text-muted">${item.original.toFixed(2)}</del>
                    </div>
                  </div>
                </div>
                <Button variant="outline-dark" size="sm">Add</Button>
              </Card.Body>
            </Card>
          ))}
    </div>
  )
}

export default UpsellItems
