import React from "react";
import { Container, Form, InputGroup, Button } from "react-bootstrap";

const HeroSearch = () => {
  return (
    <div
      style={{
        backgroundImage:
          'url("https://cache.handy-client-assets.com/taxonomy-service/service_vacation_rental_cleaning.jpg")', // Replace with your actual image
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "5rem",
        paddingBottom: "5rem",
        minHeight: "60vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Container
        style={{
          maxWidth: "700px",
          backgroundColor: "white",
          padding: "3rem 2rem",
          borderRadius: "10px",
          boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
        }}
      >
        <h2 className="text-center fw-bold mb-4">
          Choose a service to get started.
        </h2>
        <InputGroup>
          <Form.Control
            placeholder='Search for a service (e.g. "cleaning")'
            className="py-2"
          />
          <Button variant="info" className="text-white px-4">
            üîç
          </Button>
        </InputGroup>
      </Container>
    </div>
  );
};

export default HeroSearch;
