import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';
import HomePage from './pages/Home.js';
import TheBrigade from './pages/TheBrigadePage/TheBrigade.js';
import Studio from './pages/StudioPage/Studio.js';
import GlobalOverlay from './components/GlobalOverlay';
// import Floatbutton from './Floatbutton';

const App = () => {

   useEffect(() => {
    AOS.init({
      duration: 3000,  // Animation duration in ms
      twice: true       // Animate only once
    });
  }, []);


const handleClick = (e) => {
  const el = e.currentTarget;
  el.setAttribute('data-aos', 'flip-left');
  el.classList.remove('aos-animate'); // Reset animation
  void el.offsetWidth; // Force reflow
  el.classList.add('aos-animate'); // Replay animation
};
 const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate startup loading
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  return (

    <div> 
      <Router>
        <GlobalOverlay show={loading} message="Loading site..." />
      {!loading && (
      <Routes>
      <Route path="/" element={<HomePage />} />
      <Route path="/brigade" element={<TheBrigade />} />
      <Route path="/studio" element={<Studio />} />
      </Routes>
      )}

      </Router>
    </div>
  )
}

export default App
