import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';

const PopularServices = () => {
  const services = [
    {
      title: 'Home Cleaning',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/hanging_img-7ef860782e6ceac45cba539ce96088fdfcd229a340905f2f45c5f66518d7f4f3.png',
      items: ['Kitchen', 'Bathroom', 'Living Room']
    },
    {
      title: 'Furniture Assembly',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/tv_mounting_img-2c5add457f614fa27db18be961ac40cbe722e124bfb3e49c1105343adfab473b.png',
      items: ['Tables', 'Chairs', 'Beds']
    },
    {
      title: 'TV Mounting',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/furniture_assembly_img-016e6663e768c07e91185c00ca737ee7292df33f15715f5e63d131155683a29b.png',
      items: ['LCD', 'LED', 'Smart TVs']
    },
    {
      title: 'Wall Hanging',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/homecleaner_image-fedc37eea62743f6da98a145bfc573e8e827c5e68ff13b935857c43fb7d97f37.png',
      items: ['Art', 'Mirrors', 'Shelves']
    },
  ];

  return (
    <section className="py-4 bg-white">
      <Container>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h3 className="fw-medium mb-0">Popular</h3>
        </div>
        <p className="text-muted mb-4">
          Instantly book highly rated pros for cleaning and handyman tasks at an upfront price.
        </p>
        <Row>
          {services.map((service, index) => (
            <Col key={index} xs={6} md={3} className="mb-4">
              <Card className="h-100 border-0 shadow-sm">
                <Card.Img
                  variant="top"
                  src={`${service.img}?auto=format&fit=crop&w=600&q=80`}
                  alt={service.title}
                  style={{ height: '180px', objectFit: 'cover' }}
                />
                <Card.Body className="text-center border-top">
                  <Card.Text className="fw-semibold mb-2">{service.title} &gt;</Card.Text>

                  {/* âœ… Extra 3-column list ONLY visible on mobile */}
                  <div className="d-md-none">
                    <Row className="g-2">
                      {service.items.map((item, i) => (
                        <Col key={i} xs={4} className="text-muted small">
                          {item}
                        </Col>
                      ))}
                    </Row>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default PopularServices;
