import React, { useState } from 'react';

const FAQSection = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const faqs = [
    {
      question: "Are Toothpaste Bits good for sensitive teeth?",
      answer: [
        "Yes, our Bits are safe for sensitive teeth. We use Nano-hydroxyapatite in our Fluoride-Free formula, a mineral that's been proven to strengthen and remineralize enamel and help fight sensitivity.",
        "Read the nHAP Study here and read more information about it here.",
        "We also recommend talking to your dentist about Nano-hydroxyapatite or when making any changes to your oral care routine."
      ]
    },
    {
      question: "How do subscriptions work?",
      answer: [
        "Subscription details would go here...",
        "More information about subscriptions..."
      ]
    },{
      question: "More information about subscriptions How do subscriptions work?",
      answer: [
        "Subscription details would go here...",
        "More information about subscriptions..."
      ]
    },{
      question: "Subscription details would go here How do subscriptions work?",
      answer: [
        "Subscription details would go here...",
        "More information about subscriptions..."
      ]
    },
    // Add more FAQs as needed
  ];

  const toggleFAQ = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div style={{ 
      width:"100%",
      padding: '20px 50px',
      margin: '0 auto'
    }}>
      <h1 style={{
        fontSize: '2.7rem',
        padding:"70px",
        borderBottom: '1px solid black',
        textAlign: 'center'
      }}>FAQs</h1>
      
      {faqs.map((faq, index) => (
        <div key={index} style={{ 
          marginBottom: '15px',
          borderBottom: '1px solid black',
          paddingBottom: '15px'
        }}>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              cursor: 'pointer',
              padding: '1px 0'
            }}
            onClick={() => toggleFAQ(index)}
          >
            <h2 style={{
              fontSize: '1.35rem',
              fontWeight: '400',
              margin: 0
            }}>
              {faq.question}
            </h2>
            <span style={{
              fontSize: '1.5rem',
              transition: 'transform 0.3s ease',
              transform: activeIndex === index ? 'rotate(45deg)' : 'rotate(0)'
            }}>
              +
            </span>
          </div>
          
          {activeIndex === index && (
            <div style={{ padding: '0 0 15px 0' }}>
              {faq.answer.map((paragraph, i) => (
                <p key={i} style={{
                  fontSize: '1.1rem',
                  lineHeight: '1.8',
                  marginBottom: '15px'
                }}>
                  {paragraph}
                </p>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default FAQSection;
