import React, { useState } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CGT1Boxes = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  const towers = [
    {
      title: "Churchgate Tower 1, Lagos",
      description: "Standing tall in the city of Lagos for more than two decades, Churchgate Tower 1 blends modern with classic. It remains a preferred choice for top business leaders in Nigeria.",
      image: "https://media.istockphoto.com/id/146960535/photo/nyc-skyline-with-the-twin-towers.jpg?s=612x612&w=0&k=20&c=yRsqUJnNdkEYmCwEYvXXIg1k_I-aCHK8LCYjz3egbY8="
    },
    {
      title: "Churchgate Tower 2, Lagos",
      description: "Churchgate Tower 2 offers state-of-the-art facilities with a dynamic design that matches the evolving business landscape of Lagos.",
      image: "https://media.istockphoto.com/id/179060681/photo/twin-towers-in-new-york.jpg?s=612x612&w=0&k=20&c=gJBaGlo-XdzFnmSf-PuUfWjYhC1t08bAL86U816q-no="
    },
    {
      title: "Churchgate Plaza, Abuja",
      description: "Churchgate Plaza stands as a landmark in Abuja, offering premium business spaces and a commanding presence in the cityâ€™s skyline.",
      image: "https://media.istockphoto.com/id/471531023/photo/world-trade-centre-at-the-sheikh-zayed-road-in-dubai.jpg?s=612x612&w=0&k=20&c=VyPDafSr60TQZCsbA6nJBxfL-QoHLR5ZPfutzc3IwAc="
    }
  ];

  return (
    <div style={{
      fontFamily: 'Arial, sans-serif',
      padding: '3rem 0',
      backgroundColor: 'white'
    }}>
      {/* Header Section */}
      <Container className="mb-4">
        <Row>
          <Col md={6} className="text-end d-none d-md-block">
            <h2 style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#981c20' }}>
            </h2>
          </Col>
          <Col md={6} className="text-md-start text-center">
            <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: 'grey' }}>
              World Trade Center
            </h2>
            <h3 style={{ color: 'orange', fontSize: '1.5rem' }}>Abuja</h3>
          </Col>
        </Row>
      </Container>

      {/* Call to Action */}
      <div className="text-center my-4" style={{ border: "solid 7px rgba(152, 28, 32, 0.2)" }}>
        <Button
          className="fs-6 fw-bold py-3 px-4"
          style={{ backgroundColor: "#981c20", border: "none" }}
        >
          Visit the WTC Abuja Website
        </Button>
      </div>

      {/* Experience More Section */}
      <Container className="text-center mb-5">
        <h2 style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#981c20' }}>
          Experience More
        </h2>
      </Container>

      {/* Towers */}
      <Container>
        <Row className="g-4">
          {towers.map((tower, index) => (
            <Col key={index} md={4}>
              <div
                onMouseEnter={() => setHoveredCard(index)}
                onMouseLeave={() => setHoveredCard(null)}
                style={{
                  height: '400px',
                  position: 'relative',
                  overflow: 'hidden',
                  borderRadius: '0',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
                  cursor: 'pointer'
                }}
              >
                <img
                  src={tower.image}
                  alt={tower.title}
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    transition: 'transform 0.3s',
                    transform: hoveredCard === index ? 'scale(1.05)' : 'scale(1)'
                  }}
                />
                <div style={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  background: 'rgba(255, 255, 255, 0.92)',
                  padding: '1rem',
                  transform: hoveredCard === index ? 'translateY(0)' : 'translateY(65%)',
                  transition: 'transform 0.3s ease',
                  height: '80%',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'space-between'
                }}>
                  <div>
                    <h5 style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>{tower.title}</h5>
                    <p style={{ fontSize: '0.9rem', lineHeight: '1.4' }}>{tower.description}</p>
                  </div>
                  <Button
                    variant="outline-dark"
                    size="sm"
                    style={{
                      alignSelf: 'flex-start',
                      fontWeight: '500',
                      borderRadius: '20px',
                      padding: '0.4rem 1.2rem'
                    }}
                  >
                    View Details
                  </Button>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default CGT1Boxes;
