import React, { useState } from "react";
import { Form, Button, Card } from "react-bootstrap";

const WinningPredictionForm = ({ onAddMatch }) => {
  const [form, setForm] = useState({
    time: "",
    home: "",
    away: "",
    tip: "",
    ou: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // ✅ stop page reload

    if (!onAddMatch) {
      console.error("❌ onAddMatch not passed to form!");
      return;
    }

    onAddMatch(form); // ✅ send data to parent

    // reset form
    setForm({ time: "", home: "", away: "", tip: "", ou: "" });
  };

  return (
    <Card className="mb-3 p-3">
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-2">
          <Form.Label>Time</Form.Label>
          <Form.Control
            type="text"
            name="time"
            value={form.time}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-2">
          <Form.Label>Home Team</Form.Label>
          <Form.Control
            type="text"
            name="home"
            value={form.home}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-2">
          <Form.Label>Away Team</Form.Label>
          <Form.Control
            type="text"
            name="away"
            value={form.away}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-2">
          <Form.Label>Tip</Form.Label>
          <Form.Control
            type="text"
            name="tip"
            value={form.tip}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-2">
          <Form.Label>O/U</Form.Label>
          <Form.Control
            type="text"
            name="ou"
            value={form.ou}
            onChange={handleChange}
          />
        </Form.Group>

        <Button type="submit" variant="success">
          Add Prediction
        </Button>
      </Form>
    </Card>
  );
};

export default WinningPredictionForm;
