// HandyHomePage.jsx
import React from "react";
import { Container, Row, Col, Button, Navbar, Nav, Image } from "react-bootstrap";

const HandyHomePage = () => {
  return (
    <div>
      {/* Navbar */}
      <Navbar bg="white" expand="lg" className="px-4 py-3 border-bottom">
        <Navbar.Brand>
          <img src="https://res.cloudinary.com/demo/image/upload/handy-logo.png" alt="Handy Logo" height="30" />
        </Navbar.Brand>
        <Nav className="ms-auto gap-3">
          <Nav.Link href="#">Become a Pro</Nav.Link>
          <Nav.Link href="#">Help</Nav.Link>
          <Nav.Link href="#">Log In</Nav.Link>
        </Nav>
      </Navbar>

      {/* Hero Text */}
      <Container className="text-center my-5">
        <h2 className="fw-bold">The easy, reliable way to take care of your home.</h2>

        {/* Buttons */}
        <div className="d-flex justify-content-center gap-3 mt-4 flex-wrap">
          <Button variant="light" className="shadow px-4 py-3 d-flex align-items-center gap-2">
            <span style={{ backgroundColor: "#8dc63f", padding: "10px", borderRadius: "4px" }}></span>
            Home Cleaning
          </Button>
          <Button variant="light" className="shadow px-4 py-3 d-flex align-items-center gap-2">
            <span style={{ backgroundColor: "#00d1ca", padding: "10px", borderRadius: "4px" }}></span>
            All Services
          </Button>
        </div>
      </Container>

      {/* Cleaning & Handyman Tasks Section */}
      <Container>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-semibold">Cleaning & Handyman Tasks</h4>
          <a href="#" className="text-primary text-decoration-none fw-medium">See All &gt;</a>
        </div>

         <Row className="gy-4 mt-4">
            {[
              {
                img: 'https://images.unsplash.com/photo-1581579186985-4a741702a5af',
                alt: 'Home Cleaning'
              },
              {
                img: 'https://images.unsplash.com/photo-1597093288432-c3f83aa7057f',
                alt: 'Furniture Moving'
              },
              {
                img: 'https://images.unsplash.com/photo-1616627562160-5d8d12d56de6',
                alt: 'TV Installation'
              },
              {
                img: 'https://images.unsplash.com/photo-1597093871237-948563b9e198',
                alt: 'Wall Hanging'
              }
            ].map(({ img, alt }, idx) => (
              <Col xs={6} md={3} key={idx}>
                <img
                  src={`${img}?auto=format&fit=crop&w=400&q=80`}
                  alt={alt}
                  className="img-fluid rounded shadow-sm"
                />
              </Col>
            ))}
          </Row>
      </Container>
    </div>
  );
};

export default HandyHomePage;
