import React, { useState } from 'react';
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Impacts from './Pages/OurImpactPage/Impacts';
import ShopNow from './Pages/ShowNowpage/ShopNow';
import BiteAbout from './Pages/AboutPage/About';
import Header from './Components/Header';
import Footer from './Components/Footer';
import ToothpasteBitsFlourideFree from './Pages/ShowNowpage/ProductDescpPage/Toothpaste Bits Fluoride Free/ToothpasteBitsFluorideFree';
import BiteHome from './Pages/HomePage/Homes';
import CartPage from './BiteData/cartPage';
import CheckoutPage from './BiteData/checkoutPage';
import ShopByCategoryC from './Pages/ShowNowpage/ShopByCategoryC'
import ShopByCategoryD from './Pages/ShowNowpage/ShopByCategoryD'
import ShopByCategoryE from './Pages/ShowNowpage//ShopByCategoryE'
import ShopByCategoryF from './Pages/ShowNowpage//ShopByCategoryF'
import ShopByCategoryG from './Pages/ShowNowpage//ShopByCategoryG'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Floatbutton from './Floatbutton';



const App = () => {
   const [cartItems, setCartItems] = useState([]);

  const addToCart = (category) => {
    setCartItems([...cartItems, category]);
  };

  const removeFromCart = (categoryToRemove) => {
    setCartItems(cartItems.filter(category => category.id !== categoryToRemove.id));
  };

  return (
    <Router>
      <Header cartItems={cartItems} />
      <Routes>
      <Route path="/" element={<BiteHome addToCart={addToCart} />} />
      <Route path="/cart" element={<CartPage cartItems={cartItems} removeFromCart={removeFromCart} />} />
      <Route path="/checkout" element={<CheckoutPage cartItems={cartItems} />} />
      <Route path="/shopnow" element= {<ToothpasteBitsFlourideFree />} />
      <Route path="/shop" element= {<ShopNow addToCart={addToCart} />} />
      <Route path="/about" element= {<BiteAbout />} />
      <Routes>
      <Route path="/impact" element= {<Impacts />} />
      </Routes>
      {/* <Footer /> */}
      {/* <CartSummary /> */}
       <Route path="/shopc" element= {<ShopByCategoryC addToCart={addToCart} />} />
       <Route path="/shopd" element= {<ShopByCategoryD addToCart={addToCart} />} />
       <Route path="/shope" element= {<ShopByCategoryE addToCart={addToCart} />} />
       <Route path="/shopf" element= {<ShopByCategoryF addToCart={addToCart} />} />
       <Route path="/shopg" element= {<ShopByCategoryG addToCart={addToCart} />} />
      </Routes>
      <Floatbutton />
      <Footer />
    </Router>
  )


}

export default App
