import React from 'react';
import { Row, Col, Container } from 'react-bootstrap';

const logoUrls = [
  'https://bitetoothpastebits.com/cdn/shop/files/forbes-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/oprah-magazine-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/cosmopolitan-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/womens-health-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/vogue-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/goop-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/elle-logo-113023.png?v=1701383679&width=550',
  'https://bitetoothpastebits.com/cdn/shop/files/people-logo-113023.png?v=1701383679&width=550'
];

const MediaLogosSection = () => {
  return (
    <div className="text-center py-4 border-top border-bottom border-black">
      <Container fluid>
        {/* Desktop layout */}
        <Row className="d-none d-md-flex justify-content-center align-items-center flex-wrap gap-2 ">
          {logoUrls.map((logo, index) => (
            <Col key={index} xs="auto">
              <img src={logo} alt={`logo-${index}`} style={{ maxHeight: '30px', objectFit: 'contain' }} />
            </Col>
          ))}
        </Row>

        {/* Mobile marquee */}
        <div className="d-md-none overflow-hidden py-2">
          <div className="marquee-container">
            <div className="marquee-content">
              {[...logoUrls, ...logoUrls].map((logo, index) => (
                <img
                  key={index}
                  src={logo}
                  alt={`marquee-logo-${index}`}
                  style={{ height: '28px', marginRight: '2rem', verticalAlign: 'middle' }}
                />
              ))}
            </div>
          </div>
        </div>

        {/* Inline animation styles */}
        <style jsx>{`
          .marquee-container {
            white-space: nowrap;
            width: 100%;
            overflow: hidden;
          }
          .marquee-content {
            display: inline-block;
            animation: marquee 30s linear infinite;
          }
          @keyframes marquee {
            0% { transform: translateX(0); }
            100% { transform: translateX(-50%); }
          }
        `}</style>
      </Container>
    </div>
  );
};

export default MediaLogosSection;
