import React from "react";
import { Container } from "react-bootstrap";

const CGT2Hero = () => {
  return (
    <section
      style={{
        backgroundImage: "url('https://churchgate.com/wp-content/uploads/2022/08/New-Project-8-1-1300x650.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        position: "relative",
        color: "white",
        padding: "100px 0",
      }}
    >
      {/* Overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          backgroundColor: "rgba(168, 20, 20, 0.65)", // Dark reddish overlay
          zIndex: 1,
        }}
      ></div>

      {/* Content */}
      <Container style={{ position: "relative", zIndex: 2 }}>
        <h1 className="text-center fw-bold display-5 mb-4">
         Always in Sight. Hardly Forgotten
        </h1>
        <p className="text-center lead px-3 px-md-5 fw-medium fs-6">
          Modern for today, timeless for tomorrow. This 16-storeyed executive office building is one
           you should see for yourself. Churchgate Tower 2 won the hearts of baby boomers,
            millennials and GenZ, after its launch. It has now become the go-to destination
             for international businesses looking to expand their operations in Lagos. 
             Keeping up with the times and constantly evolving, Tower 2 features a 24-hour accessible underground 
             parking lot with valet, a concierge, and IoT capabilities that make life easier.
           Tower 2 presents a global standard high-rise business experience every day,
        </p>
      </Container>
    </section>
  );
};

export default CGT2Hero;
