import React from "react";
import { Navbar, Nav, Container, Card } from "react-bootstrap";
import { Cart, Moon } from "react-bootstrap-icons";
import { useCart } from "../pages/HomePage/CartContext";
import { Link } from "react-router-dom";


const Header = () => {
    const { cart } = useCart();
  
  return (
    <Navbar
      expand="lg"
      style={{
        background: "linear-gradient(180deg, #0a1c54,#073cc2)",
        padding: "0.5rem 1rem",
      }}
      variant="dark"
    >
      <Container fluid>
        {/* Logo & Brand */}
        <Navbar.Brand
          href="#home"
          className="d-flex align-items-center"
          style={{ color: "white", fontWeight: "bold" }}
        >
          <img
            alt="logo"
            src="https://i.supaimg.com/835ad601-ef5b-449b-a354-5cab625e4841.png" // replace with your logo
            style={{ marginRight: "10px", width:"100px",height:"30px" }}
          />
          AEpredicts.com
        </Navbar.Brand>

        {/* Collapse Toggle */}
        <Navbar.Toggle aria-controls="basic-navbar-nav" />

        {/* Links */}
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mx-auto" style={{ fontSize:"0.9rem"}}>
            <Nav.Link href="#home" style={{ color: "white" }}>
              Home
            </Nav.Link>
            <Nav.Link href="#football" style={{ color: "white" }}>
              Football
            </Nav.Link>
            <Nav.Link href="#basketball" style={{ color: "white" }}>
              Basketball
            </Nav.Link>
            <Nav.Link href="#hockey" style={{ color: "white" }}>
              Hockey
            </Nav.Link>
          </Nav>

          {/* Icons */}
          <Nav className="ms-auto d-flex align-items-center">
            <Nav.Link as={Link} to="/cart" style={{ color: "white" }}>
              <Cart size={20} />             
        <Card.Header className="fw-bold">My Bet Slip ({cart.length})</Card.Header>
            </Nav.Link>
            <Nav.Link href="#dark" style={{ color: "white" }}>
              <Moon size={20} />
            </Nav.Link>
          <Link to="/add" className="btn btn-outline-primary">View latest Winning</Link>
          <Link to="/addtips" className="btn btn-outline-primary">View Match predictions</Link>
          <Link to="/admin" className="btn btn-outline-primary">Admin</Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
