import React from "react";
import { useNavigate } from "react-router-dom";
import GermanyMatchForm from "../GermanyMatchForm";
import OtherMatchForm from "../OtherMatchForm"; // create similar to Germany form

const AdminDashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin/login");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h3>Admin Dashboard</h3>
      <button className="btn btn-danger mb-3" onClick={handleLogout}>
        Logout
      </button>

      <div className="mb-4">
        <h5>Germany Matches</h5>
        <GermanyMatchForm onAdded={() => alert("Germany match added")} />
      </div>

      <div>
        <h5>Other League Matches</h5>
        <OtherMatchForm onAdded={() => alert("Other league match added")} />
      </div>
    </div>
  );
};

export default AdminDashboard;
