import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const timelineData = [
  {
    year: "1968",
    side: "left",
    text: `Mr. Ishwardas Mahtani and his two sons founded what is today 
    known as the Churchgate Group. However, we started as a textile manufacturer 
    and large-scale importer and distributor of essential commodities.`,
  },
  {
    year: "1970s",
    side: "right",
    text: `We sought new expansion opportunities relentlessly, and this led 
    us to expand into industries like deep-sea fishing and shrimping, beer production, 
    and glass manufacturing.`,
  },
  {
    year: "1980s",
    side: "left",
    text: `We ventured into the banking and finance arenas and set up two local banks: 
    Chartered Bank P.L.C in 1988 and Regent Bank P.L.C.`,
  },
];

const Timeline = () => {
  return (
    <section
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1603791452906-cdd24585d101?ixlib=rb-4.0.3&auto=format&fit=crop&w=1470&q=80')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        position: "relative",
      }}
      className="text-white py-5"
    >
      <div
        style={{
          backgroundColor: "rgba(0,0,0,0.65)",
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 1,
        }}
      ></div>

      <Container style={{ position: "relative", zIndex: 2 }}>
        <h2 className="text-center fw-bold display-5 mb-5">Over 5 Decades of Excellence</h2>
        {timelineData.map(({ year, side, text }, index) => (
          <Row className="align-items-center mb-5" key={index}>
            {side === "left" && (
              <>
                <Col md={5} className="text-md-end text-center">
                  <h3 className="fw-bold">In {year},</h3>
                  <p className="fs-6">{text}</p>
                </Col>
                <Col md={2} className="d-none d-md-flex justify-content-center">
                  <div
                    style={{
                      borderLeft: "3px solid white",
                      height: "100%",
                      position: "relative",
                    }}
                  >
                    <div
                      style={{
                        width: "20px",
                        height: "20px",
                        borderRadius: "50%",
                        backgroundColor: "transparent",
                        border: "3px solid red",
                        position: "absolute",
                        left: "-11px",
                        top: "0",
                      }}
                    ></div>
                  </div>
                </Col>
                <Col md={5}></Col>
              </>
            )}
            {side === "right" && (
              <>
                <Col md={5}></Col>
                <Col md={2} className="d-none d-md-flex justify-content-center">
                  <div
                    style={{
                      borderLeft: "3px solid white",
                      height: "100%",
                      position: "relative",
                    }}
                  >
                    <div
                      style={{
                        width: "20px",
                        height: "20px",
                        borderRadius: "50%",
                        backgroundColor: "transparent",
                        border: "3px solid red",
                        position: "absolute",
                        left: "-11px",
                        top: "0",
                      }}
                    ></div>
                  </div>
                </Col>
                <Col md={5} className="text-md-start text-center">
                  <h3 className="fw-bold">In {year},</h3>
                  <p className="fs-6">{text}</p>
                </Col>
              </>
            )}
          </Row>
        ))}
      </Container>
    </section>
  );
};

export default Timeline;
