import React, { useState } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const FlipSection = () => {
  const [flippedCards, setFlippedCards] = useState({});

  const handleCardHover = (index) => {
    setFlippedCards(prev => ({ ...prev, [index]: !prev[index] }));
  };

  const amenities = [
    { text: "Landscaped surroundings", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "2 tennis courts", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "2 squash courts", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Swimming pool", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Fully equipped gym", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Private car parking", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Residents' Clubhouse", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Residents' Clubhouse", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
   { text: "2 squash courts", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Swimming pool", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    { text: "Fully equipped gym", image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg" },
    
  ];

  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      {/* Hero Section */}
      <Container style={{ margin: '3.5rem auto'}}>
        <Row>
          <Col md={5} className='p-5'>
            <h1 style={{ marginBottom: '2rem', fontSize: '4.5rem', fontWeight: 700 }}>
              The Iconic Gateway To <span className='text-danger'> Abuja's Central Business District </span>
            </h1>
            <p style={{ fontSize: '1.5rem', lineHeight: 1.6, marginBottom: '2rem' }}>
              Welcome to a new way of living and working. This one-of-a-kind development is reserved for the most quality-conscious business leaders of society who demand nothing short of perfection.
            </p>
            <p style={{ fontSize: '1.5rem', lineHeight: 1.6 }}>
              Phase 1 of the WTC, Abuja, which consists of the residential and commercial towers, is complete, with
              Phase 1 of the WTC, Abuja, which consists of the residential and commercial towers, is complete, with
              Phase 1 of the WTC, Abuja, which consists of the residential and commercial towers, is complete, with
            </p>
          </Col>
          <Col md={7} className='m-auto'>
            <div style={{ 
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
              gap: '1.5rem',
            }}>
              {amenities.map((amenity, index) => (
                <div 
                  key={index}
                  onMouseEnter={() => handleCardHover(index)}
                  onMouseLeave={() => handleCardHover(index)}
                  style={{
                    height: '200px',
                    width:'180px',
                  }}
                >
                  <div style={{
                    position: 'relative',
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.6s',
                    transformStyle: 'preserve-3d',
                    transform: flippedCards[index] ? 'rotateY(180deg)' : 'rotateY(0)'
                  }}>
                    {/* Front of Card */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      textAlign: 'center',
                      padding: '1rem',
                      backgroundColor: '#f8f9fa',
                      border:'none'
                    }}>
                      <Card.Body>
                        <Card.Text style={{ margin: 0 }} className='fs-5 m-auto'>{amenity.text}</Card.Text>
                      </Card.Body>
                    </Card>
                    
                    {/* Back of Card (Image) */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      transform: 'rotateY(180deg)'
                    }}>
                      <Card.Img 
                        variant="top" 
                        src={amenity.image} 
                        style={{ height: '100%', objectFit: 'cover' }} 
                      />
                    </Card>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default FlipSection;
