import React from "react";
import { Card, CardBody, CardText } from "reactstrap";
import { Button } from "react-bootstrap";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import "bootstrap/dist/css/bootstrap.min.css";

const testimonials = [
  {
    logo: "https://churchgate.com/wp-content/uploads/2022/11/MIM-Logo-Horizontal-1.png",
    text: "My experience with Churchgate is warm and receptive. They have reasonable rates and are always available to respond to any issues you may have.",
    name: "General Manager Supply chain",
    company: "OK FOODS",
  },
  {
    logo: "https://churchgate.com/wp-content/uploads/2022/11/vfs-global-logo.jpg",
    text: "One thing I like about Churchgate is the fact that its warehouses are strategically located with commensurate spaces.",
    name: "Admin Manager",
    company: "Sunlight Resources Limited",
  },
  {
    logo: "https://churchgate.com/wp-content/uploads/2023/06/OKFoods-Logo.webp",
    text: "What stands out the most to me about Churchgate is its building structures. I also love how they prioritize good sanitation practices.",
    name: "Tech Manager",
    company: "Money in Minutes",
  },
  {
    logo: "https://churchgate.com/wp-content/uploads/2022/11/sunlight.jpg",
    text: "I've had an amazing experience doing business with Churchgate. They have a good level of professionalism and take their time to get into and resolve issues...",
    name: "Country Manager",
    company: "VSF Global",
  },
];

const responsive = {
  desktop: { breakpoint: { max: 3000, min: 1024 }, items: 3 },
  tablet: { breakpoint: { max: 1024, min: 464 }, items: 1 },
  mobile: { breakpoint: { max: 464, min: 0 }, items: 1 },
};

const Testimony = () => (
  <section className="position-relative text-center">
    {/* Background Image with Overlay */}
    <div
      style={{
        backgroundImage:
          "url('https://img.freepik.com/free-photo/3d-rendering-white-wood-living-room-near-bedroom-upstair_105762-2197.jpg?ga=GA1.1.1831688669.1747417632&semt=ais_hybrid&w=740')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        backgroundRepeat: "no-repeat",
        position: "absolute",
        top: 0,
        bottom: 0,
        left: 0,
        right: 0,
        zIndex: 1,
      }}
    >
      {/* Overlay Layer */}
      <div
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          width: "100%",
          height: "100%",
        }}
      />
    </div>

    {/* Foreground Content */}
    <div className="position-relative py-5 " style={{ zIndex: 2, color: "white" }}>
      <Carousel
        arrows
        autoPlay
        autoPlaySpeed={3000}
        infinite
        responsive={responsive}
        showDots
        swipeable 
        itemClass=""
        containerClass="container"
      >
        {testimonials.map(({ logo, text, name, company }, index) => (
          <Card
            key={index}
            className="align-items-center rounded-2 shadow-sm fs-6 py-2 my-4 bg-light"
            style={{ height: "550px" }}
          >
            <CardBody className="text-center">
              <img src={logo} alt={company} className="img-fluid mb-3 p-4" style={{ maxHeight: "250px" }} />
              <CardText className="fst-italic px-2">"{text}"</CardText>
              <CardText className="fw-bold mt-4">{name}</CardText>
              <CardText>{company}</CardText>
            </CardBody>
          </Card>
        ))}
      </Carousel>

      <Button variant="danger"
        className="mt-5 px-4 py-2 fw-medium rounded-0"
        style={{ backgroundColor: "#9b1c1f", color: "white" }}
      >
        See More Stories
      </Button>
    </div>
  </section>
);

export default Testimony;
