import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';


const Product = ({category}) => {
  const [hoveredItem, setHoveredItem] = useState(null);

  return (
        <div 
                className="position-relative overflow-hidden border border-gray"
                style={{ 
                  height: '300px',
                  backgroundColor: '#f8f9fa',
                  cursor: 'pointer'
                }}
                onMouseEnter={() => setHoveredItem(category.id)}
                onMouseLeave={() => setHoveredItem(null)}
              >
                <img
                  src={category.img}
                  alt={category.title}
                  className="w-100 h-100 object-fit-cover"
                  style={{
                    transition: 'transform 0.4s ease',
                    transform: hoveredItem === category.id ? 'scale(1.05)' : 'scale(1)'
                  }}
                />
                <div 
                  className="position-absolute top-0 start-0 p-3"
                >
                  <h6 variant="primary" className="text-light fw-medium m-0 text-uppercase fs-4">
                    {category.title}
                  </h6>
                </div>
                <div
                  className="position-absolute bottom-0 start-0 end-0 text-center text-light"
                  style={{
                    transition: 'all 0.4s ease',
                    transform: hoveredItem === category.id ? 'translateY(0)' : 'translateY(100%)'
                  }}
                >
                   <Link to="/shopnow" ><Button variant="dark" size="lg" style={{ borderRadius:"0", width:'100%'}}> SHOP NOW</Button></Link>
                </div>
              </div>
  )
}

export default Product
