import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button, Badge } from "react-bootstrap";
import { Clock, Trophy, BarChart, Lightning, GraphUp, Dice5 } from "react-bootstrap-icons";
import { useCart } from "./CartContext";
import WinningPredictionForm from "../../components/WinningPredictionForm";

const WinningPredictions = () => {
  const { addPick } = useCart();
  const [predictions, setPredictions] = useState([]);

  // âœ… Fetch predictions from backend
  useEffect(() => {
    fetch("http://localhost:5000/winning-predictions")
      .then((res) => res.json())
      .then((data) => setPredictions(data))
      .catch((err) => console.error("Error fetching predictions:", err));
  }, []);

  // âœ… Handle adding a new prediction
  const handleAddMatch = (newPrediction) => {
    fetch("http://localhost:5000/winning-predictions", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newPrediction),
    })
      .then((res) => res.json())
      .then((updatedList) => setPredictions(updatedList)) // âœ… update state with backend response
      .catch((err) => console.error("Error adding prediction:", err));
  };

  const headers = [
    { label: "Time", icon: <Clock /> },
    { label: "Fixture", icon: <Trophy /> },
    { label: "H2H", icon: <BarChart /> },
    { label: "Tip", icon: <Lightning /> },
    { label: "O/U", icon: <GraphUp /> },
    { label: "Odds", icon: <Dice5 /> },
  ];

  const mobileVisible = ["Time", "Fixture", "Tip", "O/U"];

  return (
    <Container fluid className="my-3">
      {/* ðŸ”¹ Form to add new prediction */}
      <WinningPredictionForm onAddMatch={handleAddMatch} />

      {/* ðŸ”¹ Display Predictions */}
      <Card>
        <Card.Header
          style={{
            background: "linear-gradient(180deg, #0a1c54, #0b2a7a)",
            color: "white",
            fontWeight: "bold",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          Winning Predictions
          <Badge bg="dark">{predictions.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {/* Table Header */}
          <Row
            className="text-center fw-bold mb-2"
            style={{ fontSize: "0.8rem", borderBottom: "1px solid #eee" }}
          >
            {headers.map((h, i) => (
              <Col
                xs={mobileVisible.includes(h.label) ? 3 : 0}
                md={2}
                key={i}
                className={
                  mobileVisible.includes(h.label)
                    ? " pb-2"
                    : "d-none d-md-block pb-2"
                }
              >
                {h.icon} {h.label}
              </Col>
            ))}
          </Row>

          {/* Matches */}
          {predictions.map((match, idx) => (
            <Row
              key={idx}
              className="text-center align-items-center py-2 fw-bold"
              style={{ fontSize: "0.75rem", borderBottom: "1px solid #f1f1f1" }}
            >
              {/* Time */}
              <Col xs={3} md={2}>
                <strong>{match.time}</strong>
              </Col>

              {/* Fixture */}
              <Col xs={3} md={2}>
                <div>{match.home}</div>
                <div style={{ fontWeight: "bold" }}>VS</div>
                <div>{match.away}</div>
              </Col>

              {/* Tip */}
              <Col xs={3} md={2}>
                <Button
                  className="fw-bold"
                  size="sm"
                  variant="primary"
                  style={{ fontSize: "0.6rem" }}
                  onClick={() =>
                    addPick({
                      type: "Tip",
                      match: `${match.home} vs ${match.away}`,
                      value: match.tip,
                    })
                  }
                >
                  {match.tip}
                </Button>
              </Col>

              {/* O/U */}
              <Col xs={3} md={2}>
                <div
                  onClick={() =>
                    addPick({
                      type: "O/U",
                      match: `${match.home} vs ${match.away}`,
                      value: match.ou,
                    })
                  }
                  style={{
                    backgroundColor: "#f3e5f5",
                    color: "purple",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                    cursor: "pointer",
                  }}
                >
                  O/U <br /> {match.ou}
                </div>
              </Col>
            </Row>
          ))}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default WinningPredictions;
