import React, { useEffect, useState } from "react";
import { Container, Row, Col, Table, Button, Form } from "react-bootstrap";

const API = "http://localhost:5000";

const WinningPredictions = () => {
  const [matches, setMatches] = useState([]);
  const [editingId, setEditingId] = useState(null); // null = add mode
  const [newMatch, setNewMatch] = useState({
    date: "",
    league: "",
    home: "",
    away: "",
    tip: "",
    score: "",
    odd: "",
    outcome: ""
  });

  // Load matches from backend
  useEffect(() => {
    fetch(`${API}/matches`)
      .then((res) => res.json())
      .then((data) => setMatches(data))
      .catch(console.error);
  }, []);

  // Handle form change
  const handleChange = (e) => {
    setNewMatch({ ...newMatch, [e.target.name]: e.target.value });
  };

  // Start editing a row
  const startEdit = (match) => {
    setEditingId(match.id);
    setNewMatch({
      date: match.date || "",
      league: match.league || "",
      home: match.home || "",
      away: match.away || "",
      tip: match.tip || "",
      score: match.score || "",
      odd: match.odd || "",
      outcome: match.outcome || ""
    });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setNewMatch({
      date: "",
      league: "",
      home: "",
      away: "",
      tip: "",
      score: "",
      odd: "",
      outcome: ""
    });
  };

  // Add or Save (POST vs PUT)
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!editingId) {
      // ADD (POST)
      fetch(`${API}/matches`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newMatch),
      })
        .then((res) => res.json())
        .then((data) => {
          setMatches(data);
          cancelEdit();
        })
        .catch(console.error);
    } else {
      // SAVE CHANGES (PUT)
      fetch(`${API}/matches/${editingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newMatch),
      })
        .then((res) => res.json())
        .then((data) => {
          setMatches(data);
          cancelEdit();
        })
        .catch(console.error);
    }
  };

  // Delete row
  const handleDelete = (id) => {
    if (!window.confirm("Delete this match?")) return;
    fetch(`${API}/matches/${id}`, { method: "DELETE" })
      .then((res) => res.json())
      .then((data) => setMatches(data))
      .catch(console.error);
  };

  // Style outcome
  const outcomeClass = (outcome) => {
    const val = (outcome || "").toLowerCase();
    if (val === "won") return "text-success fw-bold";
    if (val === "lost") return "text-danger fw-bold";
    return "text-muted fw-bold"; // pending/void/etc
  };

  return (
    <Container className="py-5">
      <Row className="mb-3 align-items-center">
        <Col>
          <h3 className="fw-bold">Latest Winning Predictions</h3>
          <p className="text-muted">Track our successful predictions and winning streaks</p>
        </Col>
        <Col xs="auto">
          <Button variant="success">View Next Prediction</Button>
        </Col>
      </Row>

      {/* Table */}
      <Table bordered hover responsive striped>
        <thead className="table-success">
          <tr>
            <th>Date</th>
            <th>League</th>
            <th>Home</th>
            <th>Away</th>
            <th>Tip</th>
            <th>Score</th>
            <th>Odd</th>
            <th>Outcome</th>
            <th style={{ width: 150 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {matches.map((match) => (
            <tr key={match.id || `${match.home}-${match.away}-${match.date}`}>
              <td>{match.date}</td>
              <td>{match.league}</td>
              <td>{match.home}</td>
              <td>{match.away}</td>
              <td>{match.tip}</td>
              <td>{match.score}</td>
              <td>{match.odd}</td>
              <td className={outcomeClass(match.outcome)}>{match.outcome}</td>
              <td>
                <div className="d-flex gap-2">
                  <Button
                    size="sm"
                    variant="outline-primary"
                    onClick={() => startEdit(match)}
                  >
                    Edit
                  </Button>
                  <Button
                    size="sm"
                    variant="outline-danger"
                    onClick={() => handleDelete(match.id)}
                  >
                    Delete
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Add / Edit Form */}
      <h5 className="mt-4">{editingId ? "Edit Match" : "Add New Match"}</h5>
      <Form onSubmit={handleSubmit}>
        <Row className="g-2">
          <Col><Form.Control required name="date" placeholder="Date" value={newMatch.date} onChange={handleChange} /></Col>
          <Col><Form.Control required name="league" placeholder="League" value={newMatch.league} onChange={handleChange} /></Col>
          <Col><Form.Control required name="home" placeholder="Home Team" value={newMatch.home} onChange={handleChange} /></Col>
          <Col><Form.Control required name="away" placeholder="Away Team" value={newMatch.away} onChange={handleChange} /></Col>
        </Row>
        <Row className="g-2 mt-2">
          <Col><Form.Control name="tip" placeholder="Tip" value={newMatch.tip} onChange={handleChange} /></Col>
          <Col><Form.Control name="score" placeholder="Score" value={newMatch.score} onChange={handleChange} /></Col>
          <Col><Form.Control name="odd" placeholder="Odd" value={newMatch.odd} onChange={handleChange} /></Col>
          <Col><Form.Control name="outcome" placeholder="Outcome" value={newMatch.outcome} onChange={handleChange} /></Col>
        </Row>

        <div className="d-flex gap-2 mt-3">
          <Button type="submit" variant="primary">
            {editingId ? "Save Changes" : "Add Match"}
          </Button>
          {editingId && (
            <Button variant="secondary" onClick={cancelEdit} type="button">
              Cancel
            </Button>
          )}
        </div>
      </Form>
    </Container>
  );
};

export default WinningPredictions;
