import { Container, Row, Col } from "react-bootstrap";

const Footer = () => {
  const year = new Date().getFullYear();

  return (
    <footer
      style={{
        background: "linear-gradient(180deg, #0a3d2c, #0d4d3d)",
        color: "white",
        padding: "2rem 1rem",
        fontSize: "0.9rem",
      }}
    >
      <Container>
        <Row className="mb-3">
          {/* Left Column */}
          <Col md={6}>
            <p>office(at)over25tips.com</p>
            <p>Privacy policy</p>
            <p>Terms & Conditions</p>
            <p>Refund Policy</p>
