          {/* Right Column */}
          <Col md={6}>
            <p>Spanish Version</p>
            <p>Italian Version</p>
            <p>German Version</p>
            <p>Portuguese Version</p>
            <p>French Version</p>
            <p>Turkish Version</p>
            <p>Czech Version</p>
          </Col>
        </Row>

        {/* Disclaimer */}
        <Row className="text-center">
          <Col>
            <p>You must be 18 years old or over to use this site.</p>
            <p>Please gamble responsibly.</p>
            <p>Â© Copyright {year} | Over25Tips.com</p>
          </Col>
        </Row>
      </Container>
    </footer>
  );
};

export default Footer;
