import React from "react";
import { Nav, NavDropdown } from "react-bootstrap";

function ServicesDropdown() {
  return (
    <Nav>
      <NavDropdown
        title="All Services"
        id="services-dropdown"
        show={true} // We'll control this with hover
        onMouseEnter={(e) => e.target.click()} // Simulate click on hover
        onMouseLeave={(e) => {
          // Close the dropdown when mouse leaves
          const dropdown = document.querySelector(".dropdown-menu.show");
          if (dropdown) {
            const leaveTimeout = setTimeout(() => {
              const event = new MouseEvent("click", {
                view: window,
                bubbles: true,
                cancelable: true,
              });
              document.querySelector(".nav-link.dropdown-toggle").dispatchEvent(event);
              clearTimeout(leaveTimeout);
            }, 200); // Small delay to allow for moving to dropdown
          }
        }}
      >
        <NavDropdown.Item href="#cleaning">Cleaning</NavDropdown.Item>
        <NavDropdown.Item href="#installation">Installation</NavDropdown.Item>
        <NavDropdown.Item href="#handyman">Handyman</NavDropdown.Item>
        <NavDropdown.Item href="#outdoor-projects">Outdoor Projects</NavDropdown.Item>
        <NavDropdown.Item href="#home-renovations">Home Renovations NEW!</NavDropdown.Item>
        
        <NavDropdown.Divider />
        <NavDropdown.Item href="#all-services">See All Services</NavDropdown.Item>
      </NavDropdown>
    </Nav>
  );
}

export default ServicesDropdown;
