import { Button, Row, Col, Carousel, Container } from "react-bootstrap";

const Booking = () => {
  const imageGroups = [
    [
      "https://churchgate.com/wp-content/uploads/2022/12/New-Project-4-1-650x1000-1.jpg",
      "https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x1000-1.jpg",
      "https://churchgate.com/wp-content/uploads/2022/12/New-Project-3-1-650x1000-1.jpg",
    ],
    [
      "https://churchgate.com/wp-content/uploads/2022/12/New-Project-3-1-650x1000-1-1.jpg",
      "https://images.unsplash.com/photo-1581093588401-9ff9c3d2b27b",
      "https://images.unsplash.com/photo-1508923567004-3a6b8004f3d1",
    ],
  ];

  return (
    <div>
      <section className="py-5 bg-white text-center p-4">
        <Container fluid>
        <div className="my-4">
          <Button variant="inherit" className="px-5 fs-6 py-3 m-2 rounded-0 fw-medium text-light" style={{backgroundColor:"#661414"}}>
            Book A Tour
          </Button>
        </div>

        <Row data-aos="flip-left" className="align-items-center">
          <Col md={4} xs={12} className="text-start px-5">
            <p className="">
              A half-century is a long time, but the Churchgate group has continuously
              delivered on its promise to enhance the productivity and economic growth of
              Nigeriaâ€™s most successful entities by providing choice properties across the
              country.
            </p>
            <Button variant="danger" className=" rounded-0 mt-5 py-2 px-4 fw-medium text-light" style={{backgroundColor:"#661414"}}>
              More About Us
            </Button>
          </Col>

          <Col md={8} className="pl-5">
            <Carousel controls indicators>
              {imageGroups.map((group, idx) => (
                <Carousel.Item key={idx}>
                  <Row className="">
                    {group.map((src, i) => (
                      <Col xs={4} key={i} className="p-3">
                        <img
                          src={src}
                          alt={`Building ${i + 1}`}
                          className="img-fluid mb-3"
                          style={{ height: "400px", objectFit: "cover" }}
                        />
                      </Col>
                    ))}
                  </Row>
                </Carousel.Item>
              ))}
            </Carousel>
          </Col>
        </Row>
        </Container>
      </section>
    </div>
  );
};

export default Booking;
