import React from 'react';
import { Container, Row, Col, Carousel} from 'react-bootstrap';

// Carousel image array
const carouselImages = [
  {
    src: 'https://images.unsplash.com/photo-1486406146926-c627a92ad1ab?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
    alt: 'Churchgate Plaza 1',
  },
  {
    src: 'https://images.unsplash.com/photo-1497366811353-6870744d04b2?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
    alt: 'Churchgate Plaza 2',
  },
  {
    src: 'https://images.unsplash.com/photo-1555529669-e69e7aa0ba9a?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
    alt: 'Churchgate Plaza 3',
  },
];

const WCUCards = () => {
  return (
    <Container fluid className="py-5 px-4 bg-light">
      <Row className="align-items-center flex-column flex-md-row">
        {/* Carousel first on small screens */}
        <Col md={6} className="p-0 order-1 order-md-2 mb-4 mb-md-0">
          <Carousel controls={false} indicators={false} fade>
            {carouselImages.map(({ src, alt }, index) => (
              <Carousel.Item
                key={index}
                style={{ transition: 'opacity 1s ease-in-out' }}
              >
                <img
                  className="d-block w-100"
                  src={src}
                  alt={alt}
                  style={{
                    height: '100%',
                    objectFit: 'cover',
                  }}
                />
              </Carousel.Item>
            ))}
          </Carousel>
        </Col>

        {/* Text second on small screens */}
        <Col md={6} className="order-2 order-md-1 p-xs-0" style={{ padding:"2rem"}}>
          <h1 className="fw-bold display-6 display-md-4 mb-4" style={{ color:"#661414"}}>
            The Best Address You’ll Ever Need.
          </h1>
          <p className="display-7 fs-md-4 text-dark">
            Situated in the heart of Abuja’s business hub, Churchgate Plaza is
            distinguished by its strategic positioning within a well-serviced
            business district. The Churchgate Plaza takes things up a notch by
            offering comfortable spaces that can be converted to suit various
            business and corporate needs.
          </p>
        </Col>
      </Row>
    </Container>
  );
};

export default WCUCards;
