import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Nav
} from "react-bootstrap";

const AuthForm = () => {
  const [mode, setMode] = useState("login"); // modes: "login" or "register"

  const toggleMode = () =>
    setMode((prev) => (prev === "login" ? "register" : "login"));

  const handleSubmit = (e) => {
    e.preventDefault();
    // Replace with real logic: login or register
    alert(`${mode === "login" ? "Logging in" : "Registering"}...`);
  };

  return (
    <Container
      fluid
      className="d-flex align-items-center justify-content-center min-vh-100"
      style={{ backgroundColor: "#f8f9fa" }}
    >
      <Row className="w-100 justify-content-center">
        <Col xs={12} sm={8} md={5} lg={4}>
          {/* Header with toggle */}
          <Nav variant="tabs" fill activeKey={mode} className="mb-4">
            <Nav.Item>
              <Nav.Link eventKey="login" onClick={toggleMode}>
                Login
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="register" onClick={toggleMode}>
                Register
              </Nav.Link>
            </Nav.Item>
          </Nav>

          {/* Form */}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="email" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control type="email" placeholder="your@example.com" required />
            </Form.Group>

            <Form.Group controlId="password" className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" required />
            </Form.Group>

            {mode === "register" && (
              <Form.Group controlId="confirmPassword" className="mb-3">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control type="password" placeholder="Confirm Password" required />
              </Form.Group>
            )}

            <Button
              type="submit"
              variant="primary"
              className="w-100"
            >
              {mode === "login" ? "Login" : "Register"}
            </Button>
          </Form>

          {/* Toggle prompt */}
          <div className="text-center mt-3 small">
            {mode === "login"
              ? "Don't have an account? "
              : "Already have an account? "}
            <Button variant="link" onClick={toggleMode} className="p-0">
              {mode === "login" ? "Register" : "Login"}
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default AuthForm;
