import React from 'react';
import { Card, Container } from 'react-bootstrap';

const ChurchGatePage = () => {
  // Responsive inline styles for the overlay card
  const cardStyle = {
    top: '1rem',
    right: '1rem',
    width: '200px',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
  };

  const cardStyleMobile = {
    top: '0.5rem',
    right: '0.5rem',
    width: '150px',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
  };

  // Use media query in JS to adapt card size for small screens
  const isMobile = window.innerWidth <= 576;
  const appliedCardStyle = isMobile ? cardStyleMobile : cardStyle;

  return (
    <Container fluid className="mt-4 p-0 d-flex justify-content-center">
      <div className="position-relative" style={{ width: '50vw' }}>
        <img
          src="https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x650.jpg"
          alt="Church Gate"
          className="img-fluid w-100 rounded"
        />
        <Card className="position-absolute shadow" style={appliedCardStyle}>
          <Card.Body className="py-2 px-3">
            <Card.Title className="mb-0 h6">Church Gate 2</Card.Title>
          </Card.Body>
        </Card>
      </div>
    </Container>
  );
};

export default ChurchGatePage;
