import React, { useEffect, useState } from 'react';
import { Container, Card } from 'react-bootstrap';

const ChurchgateOverlay = () => {
  const [isMobile, setIsMobile] = useState(false);

  // Detect screen size for responsiveness
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768); // md breakpoint
    };
    handleResize(); // on mount
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Styles for the card
  const cardStyle = {
    top: isMobile ? 'auto' : '50%',
    bottom: isMobile ? '-2rem' : 'auto',
    right: isMobile ? '0' : '-9rem',
    left: isMobile ? '50%' : 'auto',
    transform: isMobile ? 'translateX(-50%)' : 'translateY(-50%)',
    backgroundColor: 'rgba(208, 209, 208, 0.5)',
    padding: '2rem',
    border: 'none',
    minWidth: isMobile ? '80vw' : '400px',
    minHeight: isMobile ? 'auto' : '350px',
    maxWidth: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  return (
    <Container fluid className="p-0 d-flex mt-4">
      <div className="position-relative" style={{ width: '70vw' }}>
        <img
          src="https://churchgate.com/wp-content/uploads/2022/11/Churchgate-Towers-12-1200x650.jpg"
          alt="Churchgate Tower"
          className="img-fluid w-100 rounded"
        />

        <Card className="position-absolute shadow" style={cardStyle}>
          <Card.Body className="text-center">
            <Card.Title
              style={{
                color: '#8B0000',
                fontWeight: 'bold',
                fontSize: '2rem',
              }}
            >
              Churchgate<br />Tower 2
            </Card.Title>
          </Card.Body>
        </Card>
      </div>
    </Container>
  );
};

export default ChurchgateOverlay;
