import React from "react";
import { Container, Row, Col, Button, Card, Badge } from "react-bootstrap";
import {
  Clock,
  Trophy,
  BarChart,
  Lightning,
  GraphUp,
  Dice5,
} from "react-bootstrap-icons";

const MatchCard = () => {
  // League + Match Data
  const league = {
    name: "England Premier League",
    flag: "https://flagcdn.com/w20/gb.png",
    matches: [
      {
        time: "04:30",
        home: "Leeds",
        away: "Everton",
        h2h: [0, 1, 0, 2, 1, 1],
        form: ["W", "L", "D", "W", "W", "L"],
        odds: {
          main: [2.25, 3.40, 3.20], // 1, X, 2
          double: [1.36, 1.30, 1.62], // 1X, 12, X2
        },
        tip: "1x",
        stat: { home: "23%", away: "77%" },
        ou: "un3.5",
      },
    ],
  };

  // Table Headers
  const headers = [
    { label: "Time", icon: <Clock /> },
    { label: "Fixture", icon: <Trophy /> },
    { label: "H2H", icon: <BarChart /> },
    { label: "Form", icon: <GraphUp /> },
    { label: "Odds", icon: <Dice5 /> },
    { label: "Tip", icon: <Lightning /> },
    { label: "Stat", icon: <BarChart /> },
    { label: "O/U", icon: <GraphUp /> },
  ];

  // Headers to show on mobile (choose 3 only)
  const mobileVisible = ["Time", "Fixture", "Tips", "Odds"];

  return (
    <Container className="my-4">
      <Card
        style={{
          borderRadius: "12px",
          overflow: "hidden",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        {/* League Header */}
        <Card.Header
          style={{
            background: "linear-gradient(90deg, #0a1c54, #0b2a7a)",
            color: "white",
            fontWeight: "bold",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <div className="d-flex align-items-center gap-2">
            <img
              src={league.flag}
              alt="flag"
              style={{ width: "24px", height: "16px" }}
            />
            {league.name}
          </div>
          <Badge bg="dark">{league.matches.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {/* Table Header */}
          <Row
            className="text-center fw-bold mb-2"
            style={{ fontSize: "0.85rem", borderBottom: "1px solid #eee" }}
          >
            {headers.map((h, i) => (
              <Col
                xs={4}
                md={i === 1 ? 2 : 1}
                key={i}
                className={
                  mobileVisible.includes(h.label) ? "" : "d-none d-md-block"
                }
              >
                {h.icon} {h.label}
              </Col>
            ))}
          </Row>

          {/* Matches */}
          {league.matches.map((match, idx) => (
            <Row
              key={idx}
              className="text-center align-items-center py-2"
              style={{ fontSize: "0.9rem", borderBottom: "1px solid #f1f1f1" }}
            >
              {/* Time */}
              <Col xs={4} md={1}>
                <strong>{match.time}</strong>
              </Col>

              {/* Fixture */}
              <Col xs={4} md={2}>
                <div>{match.home}</div>
                <div style={{ fontWeight: "bold" }}>VS</div>
                <div>{match.away}</div>
              </Col>

              {/* H2H */}
              <Col xs={12} md={2} className="d-none d-md-block">
                <div className="d-flex justify-content-center gap-1">
                  {match.h2h.map((num, i) => (
                    <div
                      key={i}
                      style={{
                        width: "20px",
                        height: "20px",
                        backgroundColor: "#e3f2fd",
                        fontSize: "0.7rem",
                        borderRadius: "3px",
                      }}
                    >
                      {num}
                    </div>
                  ))}
                </div>
              </Col>

              {/* Form */}
              <Col xs={12} md={2} className="d-none d-md-block">
                <div className="d-flex justify-content-center gap-1">
                  {match.form.map((f, i) => (
                    <span
                      key={i}
                      style={{
                        color:
                          f === "W" ? "green" : f === "L" ? "red" : "#999",
                        fontWeight: "bold",
                      }}
                    >
                      {f}
                    </span>
                  ))}
                </div>
              </Col>

              {/* Odds */}
              <Col xs={4} md={2}>
                <div className="d-flex flex-column">
                  <div className="d-flex justify-content-center gap-2">
                    {match.odds.main.map((odd, i) => (
                      <span key={i}>{odd}</span>
                    ))}
                  </div>
                  <div className="d-flex justify-content-center gap-2">
                    {match.odds.double.map((odd, i) => (
                      <span key={i}>{odd}</span>
                    ))}
                  </div>
                </div>
              </Col>

              {/* Tip */}
              <Col xs={6} md={1} className="d-none d-md-block">
                <Button size="sm" variant="primary">
                  {match.tip}
                </Button>
              </Col>

              {/* Stat */}
              <Col xs={6} md={1} className="d-none d-md-block">
                <div
                  style={{
                    backgroundColor: "#e8f5e9",
                    color: "green",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                  }}
                >
                  Home {match.stat.home}
                </div>
                <div
                  style={{
                    backgroundColor: "#e8f5e9",
                    color: "green",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                  }}
                >
                  Away {match.stat.away}
                </div>
              </Col>

              {/* O/U */}
              <Col xs={12} md={1} className="d-none d-md-block">
                <div
                  style={{
                    backgroundColor: "#f3e5f5",
                    color: "purple",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                  }}
                >
                  O/U <br /> {match.ou}
                </div>
              </Col>
            </Row>
          ))}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default MatchCard;
