import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CGT1HeroSplitSlider = () => {
  const images = [
    'https://media.istockphoto.com/id/1053901302/photo/world-trade-centre-dubai-at-night.jpg?s=612x612&w=0&k=20&c=hgNl_t1c2W--tkuZDoUu6ndaq0LW7nMGMxMQx7OoHmc=',
    'https://media.istockphoto.com/id/2151827592/photo/new-york-city-skyline-from-liberty-island-pier-1987.jpg?s=612x612&w=0&k=20&c=_DsMekU-7vLoDyO43mewNk1bc2p4cyAmp1Uev_2mH3Q=',
    'https://media.istockphoto.com/id/1778965395/photo/exterior-of-the-wtc-transportation-hub-in-new-york-city.jpg?s=612x612&w=0&k=20&c=eqNVxUabbVlpLKBjeLwzyeq_LrJ6LRyejV-QPnZ_UWg='
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [overlayWidth, setOverlayWidth] = useState('60%');

  useEffect(() => {
    const handleResize = () => {
      setOverlayWidth(window.innerWidth < 768 ? '100%' : '60%');
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prev => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <Container fluid className="p-0">
      <Row className="g-0">
        {/* LEFT Column */}
        <Col md={8} style={{ position: 'relative', minHeight: '100vh', overflow: 'hidden' }}>
          {/* Fade Images Stack */}
          {images.map((img, index) => (
            <div
              key={index}
              style={{
                backgroundImage: `url(${img})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                opacity: index === currentIndex ? 1 : 0,
                transition: 'opacity 1s ease-in-out',
                zIndex: index === currentIndex ? 1 : 0
              }}
            />
          ))}

          {/* Overlay */}
          <div
            style={{
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              minHeight: '100vh',
              width: overlayWidth,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              padding: '2rem',
              position: 'relative',
              zIndex: 2
            }}
          >
            <h1 className="text-white text-center fw-bold lh-1" style={{ fontSize: '2.5rem' }}>
              Building Iconic<br />
              Urban<br />
              Developments That<br />
              Define Nigeria
            </h1>
          </div>
        </Col>

        {/* RIGHT Column */}
        <Col
          md={4}
          className="d-flex align-items-center"
          style={{ backgroundColor: '#981c20', padding: '3rem' }}
        >
          <div className="text-white">
            <p>
              The Churchgate Group is focused on developing landmark destinations and creating a
              positive “sense of place” in whole communities.
            </p>
            <p>
              By paying attention to the needs of our end users — such as Fortune 500 businesses
              to aspiring families and luxury retailers — we create quality environments that not
              only exceed the needs of our demanding clientele but also enhance their style of
              living in the burgeoning cities of Lagos and Abuja.
            </p>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default CGT1HeroSplitSlider;
