// src/components/LaligaMatchForm.jsx
import React, { useState } from "react";
// import laligaLogo from "../assets/laliga-logo.png"; // <-- Add logo file inside src/assets

const LaligaMatchForm = ({ onAdded }) => {
  const [form, setForm] = useState({
    time: "",
    home: "",
    away: "",
    h2h: ["", "", "", "", "", ""],
    form: ["", "", "", "", "", ""],
    odds: { main: ["", "", ""], double: ["", "", ""] },
    tip: "",
    stat: { home: "", away: "" },
    ou: "",
  });

  // Handle simple inputs
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle arrays
  const handleArrayChange = (e, field, index, subfield) => {
    if (field === "odds") {
      const updated = { ...form.odds };
      updated[subfield][index] = e.target.value;
      setForm({ ...form, odds: updated });
    } else {
      const updated = [...form[field]];
      updated[index] = e.target.value;
      setForm({ ...form, [field]: updated });
    }
  };

  // Handle stats
  const handleStatChange = (e, team) => {
    setForm({ ...form, stat: { ...form.stat, [team]: e.target.value } });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:5000/laliga", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({
      time: "",
      home: "",
      away: "",
      h2h: ["", "", "", "", "", ""],
      form: ["", "", "", "", "", ""],
      odds: { main: ["", "", ""], double: ["", "", ""] },
      tip: "",
      stat: { home: "", away: "" },
      ou: "",
    });
    if (onAdded) onAdded();
  };

  return (
    <div className="d-flex justify-content-center">
      <form
        onSubmit={handleSubmit}
        className="p-4 rounded shadow"
        style={{
          maxWidth: "600px",
          width: "100%",
          background: "#f9f9f9",
        }}
      >
        {/* Logo */}
        <div className="text-center mb-3">
          <img src={""} alt="La Liga Logo" width="80" className="mb-2" />
          <h4 className="fw-bold">La Liga Match Form</h4>
        </div>

        {/* Basic Info */}
        <h6 className="mt-3 text-primary">Basic Info</h6>
        <input
          className="form-control mb-2"
          placeholder="Match Time"
          name="time"
          value={form.time}
          onChange={handleChange}
          required
        />
        <input
          className="form-control mb-2"
          placeholder="Home Team"
          name="home"
          value={form.home}
          onChange={handleChange}
          required
        />
        <input
          className="form-control mb-2"
          placeholder="Away Team"
          name="away"
          value={form.away}
          onChange={handleChange}
          required
        />

        {/* H2H */}
        <h6 className="mt-3 text-primary">Head-to-Head (last 6)</h6>
        <div className="row">
          {form.h2h.map((val, i) => (
            <div className="col-6" key={i}>
              <input
                className="form-control mb-2"
                placeholder={`H2H ${i + 1}`}
                value={val}
                onChange={(e) => handleArrayChange(e, "h2h", i)}
              />
            </div>
          ))}
        </div>

        {/* Form */}
        <h6 className="mt-3 text-primary">Recent Form (last 6)</h6>
        <div className="row">
          {form.form.map((val, i) => (
            <div className="col-6" key={i}>
              <input
                className="form-control mb-2"
                placeholder={`Form ${i + 1}`}
                value={val}
                onChange={(e) => handleArrayChange(e, "form", i)}
              />
            </div>
          ))}
        </div>

        {/* Odds */}
        <h6 className="mt-3 text-primary">Odds (Main: 1, X, 2)</h6>
        <div className="row">
          {form.odds.main.map((val, i) => (
            <div className="col-4" key={i}>
              <input
                className="form-control mb-2"
                placeholder={["1", "X", "2"][i]}
                value={val}
                onChange={(e) => handleArrayChange(e, "odds", i, "main")}
              />
            </div>
          ))}
        </div>

        <h6 className="mt-3 text-primary">Odds (Double Chance: 1X, 12, X2)</h6>
        <div className="row">
          {form.odds.double.map((val, i) => (
            <div className="col-4" key={i}>
              <input
                className="form-control mb-2"
                placeholder={["1X", "12", "X2"][i]}
                value={val}
                onChange={(e) => handleArrayChange(e, "odds", i, "double")}
              />
            </div>
          ))}
        </div>

        {/* Tip */}
        <h6 className="mt-3 text-primary">Prediction</h6>
        <input
          className="form-control mb-2"
          placeholder="Tip (e.g. 1X)"
          name="tip"
          value={form.tip}
          onChange={handleChange}
          required
        />

        {/* Stats */}
        <h6 className="mt-3 text-primary">Stats</h6>
        <div className="row">
          <div className="col-6">
            <input
              className="form-control mb-2"
              placeholder="Home Stat (%)"
              value={form.stat.home}
              onChange={(e) => handleStatChange(e, "home")}
            />
          </div>
          <div className="col-6">
            <input
              className="form-control mb-2"
              placeholder="Away Stat (%)"
              value={form.stat.away}
              onChange={(e) => handleStatChange(e, "away")}
            />
          </div>
        </div>

        {/* O/U */}
        <h6 className="mt-3 text-primary">Over/Under</h6>
        <input
          className="form-control mb-3"
          placeholder="O/U (e.g. Under 3.5)"
          name="ou"
          value={form.ou}
          onChange={handleChange}
          required
        />

        {/* Submit */}
        <button
          className="btn btn-success w-100 fw-bold"
          type="submit"
          style={{ transition: "0.3s" }}
        >
          ➕ Add La Liga Match
        </button>
      </form>
    </div>
  );
};

export default LaligaMatchForm;
